INSERT INTO `clubs` VALUES (1, NULL, 'সেনা পরিবার কল্যাণ সমিতি', NULL, '<p>নিয়মিত প্রশিক্ষণ কার্যক্রমের পাশাপাশি&nbsp; সমিতির ছাত্রীদের ব্যক্তিগত প্রয়োজন বিবেচনায় রেখে সকল ছাত্রীদের জন্য সম্মিলিতভাবে কুকিং ও পার্সোনাল গ্রূমিং বিষয়ক বিশেষ ক্লাস পরিচালনা করা হয় য কোর্স আকারে পরিচালিত হয় না।&nbsp;</p>', 1, NULL, '2021-01-05 07:22:09', NULL, 'সেপকস কর্তৃক দর্জি কাটিং, এমব্রয়ডারি হাত ও মেশিন, ব্লক বাটিক ও হ্য্যন্ড পেইন্ট ও কম্পিউটার বিষয়ক প্রশিক্ষণ প্রদান করা হয়');
INSERT INTO `clubs` VALUES (2, NULL, 'লেডিস ক্লাব', NULL, '<h3>লেডিস ক্লাবের নিজস্ব তত্ত্বাবধায়নে সকল অঞ্চল এবং শাখা কর্তৃক বিভিন্ন বিষয়ে যেমনঃ ড্রাইভিং, ইয়োগা, ফ্রান্স ভাষা শিক্ষা ইত্যাদি কোর্স সমূহ পরিচালনা করে।</h3>', 1, NULL, '2021-01-12 09:40:06', NULL, 'লেডিস কাব বাংলাদেশ সেনাবাহিনীর একটি এতিহ্যবাহী ও গুরুত্বপূর্ণ সংগঠন। এই সংগঠনটি এর সদস্যাদের মধ্যে সৌহাদ্য বৃদ্ধি করা ছাড়াও সদস্যাদের আর্থ–সামাজিক উন্নয়নে ভূমিকা রাখার জন্য উদ্ধুদ্ধ করে থাকে।');
INSERT INTO `clubs` VALUES (3, NULL, 'চিলড্রেন ক্লাব', NULL, '<h2>চিলড্রেন ক্লাব শিক্ষার্থীদের সুপ্ত প্রতিভা বিকাশের লক্ষ্যে চিলড্রেন ক্লাব কর্তৃক নাচ, গান, চিত্রাংকন/ক্রাফট, আবৃত্তি, বাদ্যযন্ত্র, ভাষা শিক্ষা, কারাতে, টাইপিং এবং অন্যান্য বিষয়ে প্রশিক্ষণ দেয়া হয়।</h2>', 1, NULL, '2021-01-06 06:05:59', NULL, 'সেনানিবাস চিলড্রেন ক্লাব সেনাবাহিনীর একটি গুরুত্বপূর্ণ সংগঠন। শিশুদের মধ্যে বন্ধুত্বসুলভ সম্পর্ক সৃষ্টি, সহমমিতা ও সহযোগিতার মনোভাব গড়ে তোলা, সাংস্কৃতিক চর্চা সর্বোপরি শিশুদের সুপ্ত প্রতিভা বিকাশের লক্ষ্যে এই ক্লাব প্রতিষ্ঠা করা হয়।');
