INSERT INTO `education` VALUES (1, 4, NULL, NULL, NULL, '<p><strong><u>কলকন্ঠ ক্লাব কর্তৃক পরিচালিত ক্লাসসমূহঃ </u></strong></p>\r\n<p>ক। ভাষা শিক্ষা</p>\r\n<p>খ। নাচ</p>\r\n<p>গ। গান</p>\r\n<p>ঘ। চিত্রাংকন</p>\r\n<p>ঙ। কবিতা আবৃতি</p>\r\n<p><strong><u>ভর্তির যোগ্যতাঃ</u></strong> সেনাবাহিনীতে কর্মরত জেসিও/ওআর এবং বেসামরিক কর্মচারীদের সন্তান যাদের বয়স ০৫&ndash;১২ বছর</p>\r\n<p><strong><u>কোর্স/ক্লাস পরিচালনা জন্য শিক্ষার্থীর সংখ্যাঃ</u></strong> কলকন্ঠ যে কোন ক্লাস পরিচালনা ক্ষেত্রে ছাত্র/ছাত্রীর সংখ্যা কমপক্ষে ১০ জন হতে হবে। এছাড়া একটি সেশনে সকল ক্লাস মিলে সবচ্চো ১০০ জন ছাত্র/ছাত্রী ভর্তি করানো হবে।</p>\r\n<p><strong><u>ভর্তি ফরমের মূল্য এবং কোর্স ফিঃ</u></strong> &nbsp;প্রতিটি ভর্তি ফরমের মূল্য ১০ টাকা এবং প্রতি বিষয়ে কোর্স ফি ২০০ টাকা অর্থাৎ প্রতি বিষয়ে সর্বমোট ২১০ টাকা</p>\r\n<p><strong><u>ক্লাসের সময়সুচীঃ</u></strong> &nbsp;কলকন্ঠ একই দিনে ০২ এ অধিক বিষয়ে ক্লাস পরিচালনা করা যাবে না।</p>', 1, '2021-01-17 07:29:38', '2021-01-21 06:57:59', 1);
