INSERT INTO `education` VALUES (17, 5, NULL, NULL, NULL, '<p><span style=\"text-decoration: underline; font-size: 24pt;\"><strong>শিক্ষা কার্যক্রম পরিচালনা</strong></span></p>\r\n<p><span style=\"font-size: 18pt;\">ক। <span style=\"text-decoration: underline;\"><strong>স্কুলের সময়সূচী</strong></span>। সরকারী ছুটি ব্যতীত প্রতি সপ্তাহে রবি, মঙ্গল ও বৃহস্পতিবার সকাল ০৯০০ ঘটিকা হতে ১২৩০ ঘটিকা পর্যন্ত (১৫ মিনিটের বিরতিসহ) ক্লাস পরিচালিত হবে।&nbsp;</span></p>\r\n<p><span style=\"font-size: 18pt;\">খ। <span style=\"text-decoration: underline;\"><strong>পাঠ্যপুস্তক ও সিলেবাস</strong></span>। পাঠ্যপুস্তক ও সিলেবাস পরিচালনা পর্ষদের অনুমোদন অনুযায়ী।</span></p>\r\n<p><span style=\"font-size: 18pt;\">গ। <span style=\"text-decoration: underline;\"><strong>শিক্ষার বিষয়াদি</strong></span>। প্রতিভা স্কুলে মূলত বাংলা, ইংরেজী এবং গনিত বিষয়ে শিক্ষা প্রদান করা হবে। এছাড়া পরিচালনা পর্ষদের সিদ্ধান্ত অনুযা়ী ছাত্রীদের নৈতিক শিক্ষা প্রদান এবং বিকাশে অন্যান্য বিষয়াদি&nbsp; অন্তর্ভূক্ত থাকবে।</span></p>', 1, '2021-01-25 12:53:33', '2021-01-31 03:32:21', 1);
