INSERT INTO `sobanetries` VALUES (1, 1, '-2024-11-04-67282abd6578d.jpg', 1, '2020-12-26 12:18:34', '2024-11-04 02:01:22', '১৯ নভেম্বর ২০২৩', '', 1, 1, 3, NULL, '<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">সেনা পরিবার কল্যাণ সমিতি (সেপকস), একটি স্বপ্নের সংগঠন ।&nbsp; ১৯৭৫ সালে প্রতিষ্ঠিত এই সংগঠন গুটি গুটি পায়ে অনেক চড়াই উতরাই এর মধ্যে দিয়ে সারা দেশব্যাপী বিস্তত হয়েছে। তারই ধারাবাহিকতায় ২৮ অক্টোর ১৯৭৫&nbsp; সালে সেনা পরিবার কল্যাণ সমিতি ঢাকা সেপকস যাত্রা শুরু করেছিল। ক্ষুদ্র প্ররিসরে পদার্পণ করে স্বল্প সময়ের মধ্যেই প্রতিষ্ঠানটি শিক্ষামূলক,কল্যাণমূলক,প্রশিক্ষণমূলক, সামাজিক ও উৎসবমুখর কর্মকান্ডে কর্মরত সেনা সদস্যদের পরিবারের কাছে বেশ জনপ্রিয় হয়ে ওঠে । বর্তমানে অঞ্চলটিতে কেন্দ্রিয় সেপকস এর নির্দেশনায় দর্জি কাটিং, এ্যামব্রয়ডারী (মেশিন ও হাত) এবং কম্পিউটার প্রশিক্ষণ চলমান রয়েছে । সেনা সদস্যদের পরিবারবর্গ এবং তাদের উপর নির্ভরশীল সদস্যদের (স্ত্রী,বোন) স্বাবলম্বী,কর্মক্ষম,আত্নপ্রত্যয়ী করার লক্ষ্যে সেনা&nbsp; পরিবার কল্যাণ সমিতি, ঢাকা অঞ্চল নিরলস ভাবে কাজ করে যাচ্ছে । আমি সেনা পরিবার কল্যাণ সমিতি, ঢাকা অঞ্চলের উত্তরোত্তর মঙ্গল এবং সকলের সুন্দর ও আনন্দময় জীবন কামনা&nbsp; করি।</span></p>', NULL, 'মিসেস দিলারা বেগম', '1', NULL);
INSERT INTO `sobanetries` VALUES (7, 1, '-2024-11-03-6727104a77529.jpg', 1, '2020-12-29 09:02:20', '2025-02-09 07:46:29', '২৪ জুন ২০২১', '', 15, 1, 1, NULL, '<div>জেনারেল জেনারেল ওয়াকার উজ&ndash;জামান, এসবিপি, ওএসপি, এসজিপি, পিএসসি</div>\r\n<div>সেনাবাহিনী প্রধান</div>\r\n<div>ও</div>\r\n<div>প্রধান পৃষ্ঠপোষক, সেনা পরিবার কল্যাণ সমিতি</div>', NULL, 'জেনারেল ওয়াকার উজ–জামান, এসবিপি, ওএসপি, এসজিপি, পিএসসি', NULL, NULL);
