INSERT INTO `sobanetries` VALUES (15, 1, '-2024-01-17-65a793ac6c833.jpg', 1, '2021-01-03 12:45:46', '2024-01-17 08:45:32', '০১ জানুয়ারী ২০২৪', '', 3, 1, 3, NULL, '<p style=\"text-align: justify;\">সেনা পরিবার কল্যাণ সমিতি (সেপকস) একটি স্বপ্নের সংগঠন। 1975 সালে প্রতিষ্ঠিত এই সংগঠনটি গুটি গুটি পায়ে অনেক চড়াই-উতরাই এর মধ্যে দিয়ে সারা দেশব্যাপী বিস্তৃত হয়েছে। তারই ধারাবাহিকতায় 1986 সালে সেনা পরিবার কল্যাণ সমিতি, সাভার অঞ্চল ক্ষুদ্র পরিসরে প্রথম পদার্পণ করে। স্বল্প সময়ের মধ্যেই প্রতিষ্ঠানটি শিক্ষা ও প্রশিক্ষণসহ নানা কল্যাণমূলক এবং সামাজিক সচেতনতামূলক কর্মকান্ডের ফলে কর্মরত সেনা সদস্যদের পরিবারের কাছে বেশ জনপ্রিয় হয়ে উঠে। বর্তমানে অঞ্চলটিতে কেন্দ্রীয় সেপকস এর নির্দেশনায় দর্জি, এ্যামব্রয়ডারী (মেশিন ও হাত) এবং কম্পিউটার প্রশিক্ষণ চলমান রয়েছে। এছাড়াও কলকন্ঠ ক্লাবের মাধ্যমে সেনা সদস্যদের (জেসিও ও অন্যান্য পদবীর সদস্য) শিশুদের গান, নাচ এবং চিত্রাংকন শিক্ষার কার্যক্রম চলমান রয়েছে। সেনা সদস্যদের পরিবারবর্গের সদস্যাদের এবং সন্তানদের স্বাবলম্বী, কার্যক্ষম, আত্মপ্রত্যয়ী এবং স্বনির্ভর করার লক্ষ্যে সেনা পরিবার কল্যাণ সমিতি, সাভার অঞ্চল নিরলস ভাবে কাজ করে যাচ্ছে। আমি সাভার অঞ্চলের উত্তরোত্তর সমৃদ্ধি কামনা করি। সকলের জীবন সুন্দর ও আনন্দময় হউক।</p>', NULL, 'তাসরিন রাহীমা খান', '1', NULL);
