INSERT INTO `sobanetries` VALUES (24, 1, '-2024-10-28-671f388cb0def.jpg', 1, '2021-01-04 06:19:31', '2024-10-28 07:09:01', '২৩ অক্টোবর ২০২৪', '', 4, 1, 3, NULL, '<p style=\"text-align: justify;\">পরিবার ও পারিবারিক জীবন হলো সমাজ জীবনের ভিত্তিপ্রস্তর। ভালোবাসা ও মায়ায় ভরা এই পরিবারেই হয়ে থাকে সামাজিক ও নৈতিক শিক্ষার হাতেখড়ি। প্রতিটি পরিবারই যেন এক একটি মানুষ তৈরীর দূর্গ। বাংলাদেশ সেনাবাহিনী একটি বৃহৎ প্রাতিষ্ঠানিক পরিবার। এ পরিবারের প্রতিটি সদস্যকে উৎকৃষ্ট মূল্যেবোধ সম্পন্ন দেশ প্রেমিক হিসেবে গড়ে তোলার দায়িত্বও বাংলাদেশ সেনাবাহিনী পালন করছে।</p>\r\n<p style=\"text-align: justify;\">সেনা পরিবারের সকল সদস্যদের মাঝে দেশ প্রেমের অমোঘ বাণী ছড়িয়ে দেয়ার জন্য প্রতিষ্ঠিত হয়েছে সেনা পরিবার কল্যাণ সমিতি (সেপকস)। দিগন্ত বিস্তৃত সাগর, পাহাড় আর সবুজ শ্যামল বনায়নে ঘেরা অপরূপ সৌন্দর্যের লীলাভূমি আমাদের রামু সেনানিবাস। এই সেনানিবাসের অভ্যন্তরে গত ২৮ অক্টোবর ২০১৫ তারিখ সেনা পরিবার কল্যাণ সমিতি (সেপকস) কক্সবাজার অঞ্চল প্রতিষ্ঠা লাভ করে। নবগঠিত এই ফরমেশনের &zwj;উন্নয়নের সাথে তাল মিলিয়ে সেনা পরিবার কল্যাণ সমিতি,কক্সবাজার অঞ্চল ধীরে ধীরে উন্নত থেকে উন্নততর হচ্ছে। সম্মানিতা প্রধান পৃষ্ঠপোষক এর বলিষ্ঠ দিক নির্দেশনায়, রামু সেনানিবাস অত্যন্ত সফল ভাবে, প্রতিষ্ঠানগুলোর কাজ পরিচলনা করে চলেছে।</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">আমি দৃঢ়ভাবে বিশ্বাস করি সেনাবাহিনীর আভ্যন্তরীন প্রতিষ্ঠানগুলো, তাদের অণির্বান স্বপ্ন বাস্তবায়নের অভিষ্ট লক্ষ্যে পৌঁছাতে সক্ষম হবে। পরিশেষে এ উদ্যোগের নেপথ্যে সংশ্লিষ্ট সকলের মেধা, শ্রম ও নিষ্ঠার জন্য সকলকে আন্তরিক ধন্যবাদ ও অভিবাদন জানাই।</p>', NULL, 'সৈয়দা মার্জিয়া বেগম', '1', NULL);
INSERT INTO `sobanetries` VALUES (25, 1, '-2023-05-22-646af71a014b2.jpeg', 1, '2021-01-04 06:23:31', '2024-11-03 14:50:21', '০৪ সেপ্টেম্বর ২০২৪', '', 5, 1, 3, NULL, '<p>বিসমিল্লাহীর রাহমানির রাহীম।</p>\r\n<div style=\"text-align: justify;\">সেনা পরিবার কল্যাণ সমিতি একটি কল্যাণধর্মী প্রতিষ্ঠান। অফিসার পত্নীদের স্বেচ্ছাপ্রণোদিত শ্রম এবং ত্যাগের উপর নির্ভরশীল হয়ে সমিতির সার্বিক কার্যক্রম পরিচালিত হয়ে আসছে। সেনা পরিবার কল্যাণ সমিতি হতে বিভিন্ন ধরনের প্রশিক্ষণমূলক কার্যক্রম ও ব্যবহারিক শিক্ষা গ্রহন করে অনেকেই আজ তাদের পরিবারের জন্য অতিরিক্ত উপার্জনে সক্ষম হয়ে উঠেছেন।<br /><br /></div>\r\n<div style=\"text-align: justify;\">আমি বিশ্বাস করি, সেপকস-এর সাথে সংশ্লিষ্ট সকলের সহযোগিতাপূর্ণ মনোভাব ও সম্মিলিত প্রচেষ্টা, আন্তরিকতা এবং একাগ্রতা থাকলে এই সমিতি দিন দিন আরো উন্নতি লাভ করবে।<br /><br /></div>\r\n<div style=\"text-align: justify;\">ওয়েবসাইটে সেনা পরিবার কল্যাণ সমিতি বগুড়া অঞ্চল-এর যে সকল তথ্য সন্নিবেশিত হচ্ছে তা অত্যন্ত গর্বের। সমিতির প্রশিক্ষণ সুষ্ঠু ও সুন্দরভাবে পরিচালিত হোক এবং অর্জিত প্রশিক্ষণ আমাদের সকলের জন্য কল্যাণ বয়ে আনুক, এই প্রত্যাশাই রইল।</div>', NULL, 'নাহিদ শারমিন', NULL, NULL);
INSERT INTO `sobanetries` VALUES (26, 1, '-2024-11-03-67271a910f616.jpg', 1, '2021-01-04 06:25:57', '2024-11-03 06:39:13', '২৪ অক্টোবর ২০২৪', '২৬ এপ্রিল ২০২১', 6, 1, 3, NULL, '<p><span style=\"font-size: 18pt;\">বিসমিল্লাহির রাহমানির রাহিম,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 18pt;\">\"&zwnj;সেনা পরিবার কল্যাণ সমিতি\" বাংলাদেশ সেনাবাহিনীর একটি গুরুত্বপূর্ণ নারী উন্নয়ন ও কল্যাণমূখী সামাজিক সংগঠন। বাংলাদেশ সেনাবাহিনীতে কর্মরত সকল সামরিক ও বেসামরিক সদস্যের পরিবারবর্গের আর্থ-সামাজিক উন্নয়নের লক্ষ্যে ১৯৭৫ সালে সেনা পরিবার কল্যাণ সমিতি (সেপকস), তার প্রথম যাত্রা শুরু করে। এই সংগঠনটি মূলত নারী ও শিশুদের স্বাবলম্বী, কর্মক্ষম, আত্মপ্রত্যয়ী ও স্বনির্ভর করে গড়ে তোলার লক্ষ্যে নিরলসভাবে কাজ করে যাচ্ছে। এরই ধারাবাহিকতায় গত ১১ই ডিসেম্বর ২০১৯ তারিখে ৩৬০ আউলিয়ার পূণ্যভূমি সিলেট সেনানিবাসে সেনা পরিবার কল্যাণ সমিতি, সিলেট অঞ্চলের পদযাত্রা শুরু হয়। এই অল্প সময়ের মধ্যে অত্র প্রতিষ্ঠানটি শিক্ষা ও প্রশিক্ষণসহ&nbsp; নানাবিধ কল্যাণমূলক ও সামাজিক কর্মকান্ডের মাধ্যমে সামরিক ও বেসামরিক সদস্যদের পরিবারের মাঝে বেশ জনপ্রিয়তা লাভ করেছে। বর্তমান কেন্দ্রীয় সেপকস এর দিক-নির্দেশনা মোতাবেক দর্জি এবং কম্পিউটার প্রশিক্ষণ পরিচালনা করা হচ্ছে। এছাড়াও সেনা পরিবার কল্যাণ সমিতি, সিলেট অঞ্চলের \"কলকন্ঠ\" ক্লাব বিভিন্ন প্রশিক্ষণ যেমন নাচ, গান, আবূত্তি ইত্যাদির মাধ্যমে সেনা সদস্যদের সন্তানদের মাঝে একতার বন্ধন, সুস্থ্য সাংস্কৃতিক চর্চা, শ্রদ্ধাবোধ বৃদ্ধি, আত্মবিশ্বাসী ও মনোবল বৃদ্ধির প্রচেষ্টা অব্যাহত রেখেছে।&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 18pt;\">সেনা পরিবার কল্যাণ সমিতি এর কার্যক্রমকে গতিশীল ও যুগোপযোগী করে তোলার লক্ষে ওয়েবসাইটটি নির্মাণের জন্য আমি সম্মানিত প্রধান পৃষ্ঠপোষক, সম্মানিতা পৃষ্ঠপোষক ও এর সাথে সংশিষ্ট সকলের প্রতি আন্তরিক কৃতজ্ঞতা প্রকাশ করছি। বিশেষ করে সেনা পরিবার কল্যাণ সমিতি, সিলেট অঞ্চলের প্রশিক্ষণ ও কল্যাণমূলক কর্মকান্ড পরিচালনার পিছনে ওয়েবসাইটটি গুরুত্বপূর্ণ ভৃমিকা পালন করবে বলে আমি বিশ্বাস রাখি। আমি সেনা পরিবার কল্যাণ সমিতি, সিলেট অঞ্চলের উত্তরোত্তর উন্নতি, অগ্রগতি ও সমৃদ্ধি কামনা করি। মহান রাব্বুল-আলামিন আমাদের সকলের সহায় হউন। আমিন।&nbsp;</span></p>', NULL, 'নাহিদ রিদওয়ান', NULL, NULL);
