INSERT INTO `sobanetries` VALUES (74, 3, '-2023-09-24-650fb1b53cd6f.jpg', 1, '2021-01-06 08:22:57', '2023-09-24 03:52:42', '১৮ অগাস্ট ২০২৩', '', 8, 3, 3, NULL, '<div style=\"text-align: justify;\">চট্টগ্রাম এরিয়া চিলড্রেন্&zwnj; ক্লাব বাংলাদেশ সেনাবাহিনীর একটি গৌরবময় এবং সুপ্রতিষ্ঠিত সংগঠন। সূচনালগ্ন থেকেই চট্টগ্রাম এরিয়া চিলড্রেন্&zwnj; ক্লাব সেনাসদস্যদের সন্তানের মানসিক বিকাশ ও উন্নয়নের জন্য কাজ করে যাচ্ছে। লেখাপড়ার পাশাপাশি শিশুদের মেধাবিকাশে এবং সাংস্কৃতিক কর্মকান্ডে উদ্বুদ্ধ করায় এই সংগঠন অনন্য ভূমিকা পালন করে আসছে। আমাদের সন্তানেরা যেন সুযোগ্য এবং সুপ্রতিষ্ঠিত হয়ে বেড়ে উঠতে পারে তারই প্রচেষ্টায় নিয়োজিত এই চিলড্রেন ক্লাব। ছোট সোনামনিদের স্বভাবিকভাবে বেড়ে ওঠা ও তাদের মেধা ও মননের বিকাশ সাধনই উক্ত সংগঠনটির প্রধান উদ্দেশ্য।<br /><br /></div>\r\n<div style=\"text-align: justify;\">চিলড্রেন ক্লাবের আয়োজনে বিগত বছরে বিভিন্ন সামাজিক অনুষ্ঠানে ছোট সোনামনিরা অনেক আনন্দঘন ও ফলপ্রসু মুহূর্ত কাটিয়েছে। জাতির জনক বঙ্গবন্ধু শেখ মুজিবুর রহমান এর জন্মশত বার্ষিকী উপলক্ষে সকলের সহযোগিতায় অনুষ্ঠিত চিত্রাংকন &nbsp;প্রতিযোগিতা ছিল সত্যিই মনোমুগ্ধকর। সকলের&nbsp; আন্তরিকতা, ভালোবাসা, আর&nbsp; সহযোগিতার ফলে চট্টগ্রাম এরিয়া চিলড্রেন ক্লাবের যাবতীয় কার্যক্রম অত্যন্ত সুন্দর ও সফলভাবে সম্পন্ন হয়েছে।<br /><br /></div>\r\n<div style=\"text-align: justify;\">চট্টগ্রাম এরিয়া চিলড্রেন্&zwnj; ক্লাবের প্রধান পৃষ্ঠপোষক নুরজাহান আহমেদ এর দিকনির্দেশনা এবং নেতৃত্বে আমাদের চট্টগ্রাম এরিয়া চিলড্রেন্&zwnj; ক্লাবের সাংগঠনিক কাঠামো এবং আনুষ্ঠানিক কার্যাবলীতে উত্তরোত্তর উন্নতি সাধিত হয়েছে। এছাড়াও, চট্টগ্রাম এরিয়া চিলড্রেন্&zwnj; ক্লাবের বিগত সকল সভানেত্রীদের নিরলস প্রচেষ্টা এবং নেতৃত্বে আমদের সংগঠনটি আজ অনেক দূর এগিয়ে এসেছে। পাশাপাশি সকল সহ-সভানেত্রী, সাধারন সম্পাদিকা, কোষাধ্যক্ষা এবং সকল সদস্যাদের অকৃত্রিম ভালোবাসা এবং একনিষ্ঠতায় চট্টগ্রাম এরিয়া চিলড্রেন্&zwnj; ক্লাব আজ সুপ্রতিষ্ঠিত এবং গৌরবজ্জল একটি সংগঠন । <br />&nbsp;</div>\r\n<div style=\"text-align: justify;\">চিলড্রেন ক্লাবের বিভিন্ন কার্যক্রমে অংশগ্রহনের মাধ্যমে ছোট সোনামনিরা উত্তরোত্তর উন্নতি করে যেন উজ্জল ভবিষ্যতের পানে অগ্রসর হয় সেই প্রার্থনা করি। পরম করুনাময় আল্লাহ তাআলা আমাদের সকলের সহায় হউন। আমিন।&nbsp;&nbsp;</div>', NULL, 'ফাতেমা তুজ জোহরা', '1', NULL);
INSERT INTO `sobanetries` VALUES (106, 2, '-2023-10-02-651a28dd28c48.jpeg', 1, '2021-01-06 08:48:08', '2023-10-02 02:20:13', '১৪ নভেম্বর ২০২১', '', 12, 2, 3, NULL, '<p style=\"text-align: justify;\">সুস্থ পারিবারিক পরিবেশ কর্মক্ষেত্রে আনে সফলতা। লেডিস ক্লাব সেনাবাহিনীতে কর্মরত অফিসার পত্নীগণের প্রাণের একটি সংগঠন। এ সংগঠনের মাধ্যমে অত্র ক্লাবের সদস্যাগণকে আধুনিক ও যুগোপযোগী করে গড়ে তোলা হয়। বাংলাদেশ মিলিটারি একাডেমিতে ২০ মে ২০১৩ সালে স্বতন্ত্র লেডিস ক্লাব প্রতিষ্ঠা করা হয়। সূচনালগ্ন হতে বিএমএ লেডিস ক্লাব অত্র একাডেমিতে কর্মরত অফিসার পত্নীগণের মেধা, মননশীলতা এবং সাংস্কৃতিক অঙ্গনে সুপ্ত প্রতিভা বিকাশের লক্ষ্যে বিভিন্ন বিনোদনমূলক, শিক্ষামূলক অনুষ্ঠানের আয়োজন এবং সমকালীন অন্যান্য বিষয়ের উপর প্রশিক্ষণ পরিচালনা করে আসছে।</p>\r\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; সেনাবাহিনীর অফিসার পত্নীগণের মাঝে সেনাবাহিনীর মূল্যবোধ ও চেতনা, নিয়ম শৃঙ্খলা, আচার-আচরণ ও ঐতিহ্য শিক্ষা দেয়া, পারস্পরিক সুসম্পর্ক তৈরি ও যোগাযোগ বৃদ্ধি এবং সকল সদস্যাগণের মাঝে বন্ধুত্বপূর্ণ সম্পর্ক তৈরি করার লক্ষ্যে অত্র সংগঠন নিরলসভাবে কাজ করে যাচ্ছে। বিভিন্ন জাতীয় ও আন্তর্জাতিক দিবস পালন, মেহেদী উৎসব, বনভোজন, আনন্দ ভ্রমণ, পহেলা বৈশাখ এবং বিভিন্ন গুর&brvbar;ত্বপূর্ণ দিবসে সাংস্কৃতিক অনুষ্ঠান ও প্রতিযোগিতা আয়োজনের মাধ্যমে লেডিস ক্লাব গুর&brvbar;ত্বপূর্ণ ভূমিকা রাখছে। এছাড়াও এ সংগঠন জাতীয় বিভিন্ন দূর্যোগে দুঃস্থ জনগোষ্ঠীকে সহায়তা প্রদান করে আসছে।</p>\r\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;লেডিস ক্লাব, বিএমএ অঞ্চলের সভানেত্রী হিসাবে মহান আল্লাহ তায়ালার নিকট প্রার্থনা করি, আমরা যেন এই প্রতিষ্ঠানের অগ্রগতির ধারাকে অক্ষুণ্ন রেখে আমাদের লেডিস ক্লাবের সকল সদস্যাগণকে যুগোপযোগী করে গড়ে তুলতে পারি। লেডিস ক্লাব, বিএমএ অঞ্চলের উত্তরোত্তর উন্নতি, অগ্রগতি ও সমৃদ্ধি কামনা করছি।</p>', NULL, 'মিসেস রহিমা কামরুল', NULL, NULL);
