INSERT INTO `sobanetries` VALUES (143, 1, '-2024-11-07-672c708b038bb.jpg', 1, '2021-01-07 02:30:21', '2024-11-07 07:47:23', '২১ অক্টোবর ২০২৪', '', 8, 1, 3, NULL, '<p>পাহাড় ও সমুদ্রঘেরা অপূর্ব প্রাকৃতিক সৌন্দর্যের লীলাভূমি চট্টগ্রামে অবস্থিত চট্টগ্রাম সেনানিবাসে ১৯৮৫ সালে সর্বপ্রথম সেনা পরিবার কল্যাণ সমিতি তার যাত্রা শুরু করে।&nbsp;</p>\r\n<p style=\"text-align: justify;\">সেনা পরিবার কল্যাণ সমিতি বাংলাদেশ সেনাবাহিনীর একটি গুরুত্বপূর্ণ নারী ও শিশু কল্যাণমূলক সংস্থা। সূচনালগ্ন থেকে সংস্থাটি সেনা পরিবারের সদস্যদের আত্মনির্ভরশীল ও স্বাবলম্বী করে তোলার কাজে নিয়োজিত রয়েছে। তারই ধারাবাহিকতায় এই সংস্থা দর্জি কাটিং, এমব্রয়ডারি ও কম্পিউটার কোর্সসহ শিশুদের জন্য কোরআন শিক্ষা, নাচ, গান ও চিত্রাঙ্কন ক্লাস এবং আরও নানাবিধ প্রশিক্ষণমূলক কর্মকান্ড পরিচালনা করে আসছে। শুধু প্রশিক্ষণ নয়, বার্ষিক বনভোজন ও বিভিন্ন প্রতিযোগিতা আয়োজনের মাধ্যমে সংস্থার বিনোদনমূলক কর্মকান্ডগুলো পরিচালিত হচ্ছে। প্রাত্যহিক কার্যক্রমের পাশাপাশি বন্যাদুর্গত এলাকায় ক্ষতিগ্রস্ত সেনা পরিবার কল্যাণ সমিতি দুস্থ ও অসহায়দের প্রতি বাড়িয়ে দিয়েছে মানবতার হাত। সেপকস, চট্টগ্রাম অঞ্চলের আওতাভুক্ত খাগড়াছড়ি, বান্দরবান, রাঙ্গামাটি, গুইমারা ও হালিশহর শাখা সেপকসসমূহ একই উদ্দীপনায় সকল প্রশিক্ষণ ও বিনোদনমূলক কার্যক্রমগুলো পরিচালনা করে চলেছে। এভাবেই বিভিন্ন ধারার কর্মকান্ডের মাধ্যমে সংস্থাটি সেনা পরিবারের সদস্যদের মাঝে শ্রদ্ধাবোধ, সহমর্মিতা ও আত্মনির্ভরশীলতা তৈরি করে তাদের স্বাবলম্বী করে তোলার প্রচেষ্টা চালিয়ে যাচ্ছে।</p>\r\n<p style=\"text-align: justify;\">আগামী দিনেও সেপকস, চট্টগ্রাম অঞ্চল ও এর শাখাগুলোর কার্যক্রম সুন্দরভাবে সম্পন্ন হবে ও অত্র অঞ্চল আরও সুখ্যাতি অর্জন করবে এই আমাদের কামনা। আমাদের সকলের পরিশ্রম ও অধ্যবসায় সেপকস, চট্টগ্রাম অঞ্চলের সফলতা বয়ে আনুক এ আশা ব্যক্ত করছি।</p>\r\n<p style=\"text-align: justify;\">পরিশেষে সেপকস এর সাথে নিযুক্ত সম্পাদনা পর্ষদ, অন্যান্য সকল অফিসার, জেসিও ও এনসিওবৃন্দকে আমি আন্তরিক শুভেচ্ছা ও অভিনন্দন জানিয়ে সকলের মঙ্গল কামনা করছি। মহান আল্লাহা আমাদের সহায় হোন। আমিন।</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', NULL, 'রেজিনা মুশফিক', NULL, NULL);
INSERT INTO `sobanetries` VALUES (149, 1, '-2024-11-03-6727346e35441.jpg', 1, '2021-01-07 02:55:59', '2024-11-03 08:30:08', '২২ অগাস্ট ২০২৪', '', 10, 1, 3, NULL, '<p>সেনা পরিবার কল্যাণ সমিতি (সেপক্&zwnj;স) একটি নারী উন্নয়ন এবং কল্যাণমুখী প্রতিষ্ঠান। সেপক্&zwnj;স যশোর অঞ্চল, এর দায়িত্বপূর্ণ এলাকায় কর্মরত জুনিয়র কমিশন্ড অফিসার, অন্যান্য পদবীর সৈনিক এবং বেসামরিক কর্মচারীদের পরিবারবর্গকে বিভিন্ন প্রকার বৃত্তিমূলক প্রশিক্ষণ প্রদানের মাধ্যমে তাদের স্বাবলম্বী করে গড়ে তোলার মহৎ কাজে নিয়োজিত।</p>\r\n<p>কল্যাণ, প্রগতি ও আত্মনির্ভরশীলতা প্রতিষ্ঠার লক্ষ্য নিয়ে সেনা পরিবার কল্যাণ সমিতি (সেপক্&zwnj;স), যশোর অঞ্চল গত ০১ জুলাই ১৯৮৭ সালে যাত্রা শুরু করে। সাইত্রিশ বছরের সুদীর্ঘ পথচলায় সেপক্&zwnj;স, যশোর অঞ্চল সেনাসদস্যদের পরিবারের স্বনির্ভরতা ও স্বচ্ছলতা নিশ্চিতকল্পে নিরলসভাবে কাজ করে যাচ্ছে। এছাড়াও, কলকন্ঠ ক্লাব, যশোর অঞ্চল অবদান রাখছে সেনাসদস্যদের সন্তানদের মাঝে একতার বন্ধন, সাংস্কৃতিক চর্চা, আত্মবিশ্বাস ও মনোবল বৃদ্ধিতে।</p>\r\n<p>অত্র সমিতি প্রতিষ্ঠার পর থেকে অদ্যাবধি দর্জি ও এ্যামব্রয়ডারী বিভাগে ৭২ টি ব্যাচ, কম্পিউটার বিভাগে ২৪ টি ব্যাচ এবং ব্লক ও বাটিক বিভাগের ২১ টি ব্যাচ প্রশিক্ষণ সমাপ্ত করেছে। অত্র সমিতি প্রতি বৎসর জানুয়ারি-জুন এবং জুলাই-ডিসেম্বর মোট ০২ টি সেশনে দর্জি, এ্যামব্রয়ডারী (হাত ও মেশিন), কম্পিউটার, ব্লক ও বাটিক, নাচ, গান, আরবি শিক্ষা এবং আর্ট প্রশিক্ষণ প্রদান করে থাকে।</p>\r\n<p>সেনা পরিবার কল্যাণ সমিতি, যশোর অঞ্চল কর্তৃক তৈরীকৃত বিভিন্ন দ্রব্য সামগ্রী ০৫ টি আউটলেটের মাধ্যমে প্রদর্শনী এবং বিক্রয় করা হয়ে থাকে। যা এ অঞ্চলের সেনা পরিবার কল্যাণ সমিতির অর্থনৈতিক কার্যক্রমকে আরও গতিশীল করেছে।</p>\r\n<p>উন্নয়নের ধারা বজায় রেখে সেনা পরিবার কল্যাণ সমিতি, যশোর অঞ্চল ভবিষ্যতের দিনগুলোতে সাফল্যের সাথে সামনের দিকে এগিয়ে যাবে এবং সামরিক পরিমন্ডলে গুরুত্বপূর্ণ অবদান রাখবে এ আশাবাদ ব্যক্ত করে শেষ করছি। পরম করুনাময় আল্লাহ তায়ালা আমাদের সকলের সহায় হউন। <strong>আমিন</strong>।</p>\r\n<p>&nbsp;</p>', NULL, 'শ্যামলী হায়দার ইমদাদ', NULL, NULL);
