INSERT INTO `sobanetries` VALUES (156, 1, '-2022-05-11-627bb7f1c0583.jpg', 1, '2021-01-07 03:09:04', '2022-05-11 13:25:24', '২৮ নভেম্বর ২০২১', '', 12, 1, 3, NULL, '<p style=\"text-align: justify;\">সেনা পরিবার কল্যাণ সমিতি (সেপকস) বাংলাদেশ সেনাবাহিনীতে কর্মরত সামরিক ও অসামরিক ব্যক্তিবর্গের পরিবারবর্গের জন্য একটি উন্নয়নমূলক সংগঠন। সেনা পরিবার কল্যাণ সমিতি, বিএমএ ২১ জুন ১৯৮৯ সালে প্রতিষ্ঠা লাভ করে। প্রতিষ্ঠার পর থেকে এই সংগঠনটি ০৪ জুন ২০১৩ পর্যন্ত চট্রগ্রাম অঞ্চলের শাখা অঞ্চল হিসেবে এর কার্যক্রম সম্পন্ন করে আসছিল। গত ০৫ জুন ২০১৩ তারিখ হতে সেনা পরিবার কল্যাণ সমিতি, বিএমএ অঞ্চল নামে পৃথক অঞ্চল হিসেবে পরিচালিত হচ্ছে। শুরু থেকেই সেনা পরিবার কল্যাণ সমিতি, বিএমএ অঞ্চল উক্ত একাডেমিতে কর্মরত জেসিও/ওআর এবং অসামরিক কর্মচারীদের পরিবারের সদস্যাদের স্বাবলম্বী, কর্মক্ষম, আত্নপ্রত্যয়ী এবং স্বনির্ভর করার লক্ষ্যে বিবিধ প্রশিক্ষণ ও কল্যাণমূলক কার্যক্রম পরিচালনা করে আসছে। অত্র অঞ্চলের সেনা পরিবারের নারী সদস্যারা দেশের সার্বিক উন্নয়ন ও নারীর ক্ষমতায়ন বৃদ্ধির লক্ষ্যে নিরলসভাবে কাজ করে যাচ্ছে। এই প্রতিষ্ঠানটি সারাবছর বিভিন্ন শিক্ষামূলক, কল্যাণমূলক, প্রশিক্ষণমূলক ও সামাজিক সচেতনতামূলক নানাবিধ কার্যক্রম পরিচালনা করে থাকে।</p>\r\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; কলকন্ঠ ক্লাব সেনা পরিবার কল্যাণ সমিতির একটি গুরুত্বপূর্ণ অংশ, যা সেনাবাহিনীতে কর্মরত জেসিও/ওআর এবং অসামরিক কর্মচারীদের সন্তানদের মেধা বিকাশের অংশ হিসেবে সহায়ক ভূমিকা পালন করছে। প্রতি বছর কলকন্ঠ ক্লাব, বিএমএ অঞ্চলে দুই সেশনে শিশুদের চিত্রাংকন প্রশিক্ষণ দেয়া হয়। এছাড়া কলকন্ঠ ক্লাব শিশুদের অংশগ্রহনে বিভিন্ন গুরুত্বপূর্ণ এবং জাতীয় দিবস সমূহে চিত্রাংকন প্রতিযোগিতা আয়োজনের মধ্যে দিয়ে শিশুদের মধ্যে প্রতিযোগিতামূলক মনোভাব তৈরী, আত্নপ্রত্যয়ী ও মননশীল করতে গুরুত্বপূর্ণ ভূমিকা পালন করছে।</p>\r\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; সেনা পরিবার কল্যাণ সমিতি, বিএমএ অঞ্চলের সভানেত্রী হিসেবে পরম করুনাময়ের কাছে প্রার্থনা করি যেন সকলকে সাথে নিয়ে এই প্রতিষ্ঠানের অগ্রগতির ধারাকে অক্ষুন্ন রেখে সামনের দিকে এগিয়ে নিয়ে যেতে পারি। সেনা পরিবার কল্যাণ সমিতি, বিএমএ অঞ্চলের সকলের জীবন সুন্দর ও আনন্দময় হোক। সবার জন্য রইল শুভকামনা।&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>', NULL, 'Mrs Rahima Kamrul', NULL, NULL);
