INSERT INTO `sobanetries` VALUES (165, 2, '-2024-11-03-6727904102fd2.jpeg', 1, '2021-01-07 04:05:13', '2024-11-03 15:01:21', '২৩ অক্টোবর ২০২৪', '০২ নভেম্বর ২০২১', 2, 2, 3, NULL, '<p style=\"text-align: justify;\">লেডিস ক্লাব বাংলাদেশ সেনাবাহিনীর ঐতিহ্য এবং কর্মকান্ডের একটি অবিচ্ছেদ্য অংশ যার প্রধান উদ্দেশ্য সকল সেনা পত্নীদের মাঝে সামাজিক সৌহার্দ্যপূর্ণ সম্পর্কের সেতুবন্ধন তৈরী করা। সেনাবাহিনীর অফিসার পরিবারবর্গের মাঝে পারস্পরিক সৌহার্দ্য, সম্প্রীতি এবং সামাজিক মেলবন্ধন দৃঢ় করতে লেডিস ক্লাবের ভূমিকা অপরিসীম। এরই ধারাবাহিকতায় নবগঠিত শেখ হাসিনা সেনানিবাসে গত ১৬ আগষ্ট ২০১৮ তারিখে আনুষ্ঠানিকভাবে লেডিস ক্লাব, বরিশাল অঞ্চলের যাত্রা শুরু হয়। বাংলাদেশ সেনাবাহিনীর অফিসার পত্নীদের দ্বারা পরিচালিত এই সুসংগঠিত ও স্বনামধন্য সংগঠনটি সামরিক অফিসার এবং তাঁদের পরিবারবর্গের সামাজিক জীবনের পরিপূর্ণ বিকাশের জন্য গুরুত্বপূর্ণ ভূমিকা পালন করে আসছে। ক্লাবের প্রতিটি সদস্যাদের মেধা ও সক্রিয়তাকে সুব্যবহার করে সর্বক্ষেত্রে তাদের অংশগ্রহণের মাধ্যমে ক্লাবটি সামরিক পরিবার মন্ডলের জন্য একটি আদর্শ&nbsp; মিলন কেন্দ্রে পরিণত হয়েছে। লেডিস ক্লাব কর্তৃক বিভিন্ন অনুষ্ঠান আয়োজনের মধ্য দিয়ে সকল সদস্যাদের মাঝে সামাজিক বন্ধন দৃঢ় হয় এবং সামরিক জীবনে একটি সুন্দর ঐকতানের সৃষ্টি হয়। উক্ত অনুষ্ঠানসমূহ অফিসার পরিবারবর্গের জীবনে ছড়িয়ে দেয় অনাবিল আনন্দ এবং এরই মাধ্যমে অফিসারদের জীবন সঙ্গীনিদের সুপ্ত প্রতিভাসমূহের বিকাশ ঘটে।</p>\r\n<p style=\"text-align: justify;\">বাংলাদেশের অন্যান্য লেডিস ক্লাবের মত বরিশাল লেডিস ক্লাবও বিনোদনমূলক কার্যক্রম এর পাশাপাশি বিভিন্ন গঠনমূলক কার্যক্রমের আয়োজন করছে। প্রতিষ্ঠালগ্ন থেকে এ ক্লাবটি বিভিন্ন সামাজিক কর্মকান্ডে গুরুত্বপূর্ণ ভূমিকা পালন করে আসছে। করোনা মহামারীতেও লেডিস ক্লাব বরিশাল অঞ্চল দুঃস্থ্য, অসহায় ও দরিদ্র মহিলাদের মাঝে ত্রাণ সহায়তা প্রদান এবং প্রতি বছরই অসহায় দরিদ্রদের মাঝে শীতবস্ত্র বিতরণ কার্যক্রম সম্পাদন করে থাকে। আমি আনন্দিত যে বরিশাল লেডিস ক্লাবের কর্মকান্ড শুধু সাংস্কৃতিক পরিমন্ডলে সীমাবদ্ধ না রেখে এটি আমাদের সামরিক ঐতিহ্যরই একটি অংশ হিসেবে দ্রুত বিকাশ লাভ করছে। ভবিষ্যতে লেডিস ক্লাব, বরিশাল অঞ্চল ধীরে ধীরে আরও উন্নতির দিকে অগ্রসর হবে এবং ক্লাবের সুনাম ও ঐতিহ্যের কথা বিবেচনায় সকল সদস্যাগণ স্বপ্রণোদিত ও আত্মপ্রত্যয়ী ভূমিকা পালন করবে বলে আমার দৃঢ় বিশ্বাস।</p>\r\n<p style=\"text-align: justify;\">পরিশেষে, আমি লেডিস ক্লাব বরিশাল অঞ্চলের পাশাপাশি বাংলাদেশ সেনাবাহিনীর সকল অফিসার পরিবারবর্গের জন্য সু-স্বাস্থ্য, দীর্ঘায়ু ও সুন্দর জীবন কামনা করছি। মহান আল্লাহ আমাদের সহায় হোন। আল্লাহ হাফেজ।</p>', NULL, 'মাহফুজা রহমান', NULL, NULL);
INSERT INTO `sobanetries` VALUES (168, 2, '-2021-01-18-6004f816b5ee5.jpg', 1, '2021-01-07 04:39:27', '2021-02-08 05:36:20', '১০ মার্চ ২০২০', '', 5, 2, 3, NULL, '<span style=\"font-family: Arial; font-size: 12pt;\"><span style=\"font-family: Arial; font-size: 12pt;\"></span></span>\r\n<div style=\"text-align: justify;\">নারীরা যখন শক্তিরূপা হয়ে ওঠে তখন পৃথিবীতে অনেক বড় পরিবর্তন আসে। এই বিশ্বাস নিয়েই, স্বমহিমা এবং নিজ কর্মদক্ষতায় বলীয়ান হয়ে সগৌরবে এগিয়ে চলেছে লেডিস ক্লাব, বগুড়া অঞ্চলের সকল কার্যক্রম। একজন নারী শুধু তার সংসারই নয়, প্রয়োজন অনুযায়ী তার সমাজকেও গড়ে তুলতে পারে তার সুনিপুন হাতের ছোঁয়ায়, তারই প্রমাণ এই ক্লাবের সাথে সম্পৃক্ত সকল সদস্যাবৃন্দ। লেডিস ক্লাব বাংলাদেশ সেনাবাহিনীর একটি ঐতিব্যবাহী সংগঠন। বঙ্গবন্ধুর প্রতিষ্ঠিত সোনার বাংলায় স্বাধীন দেশের সেনাবাহিনী পুনর্গঠনের পরপরই এই ক্লাবের অগ্রযাত্রা শুরু হয় যা ধীরলয়ে অগ্রসর হতে হতে আজকের এই সুসংগঠিত অবস্থানে এসে পৌছেছে।<br /><br /></div>\r\n<div style=\"text-align: justify;\">প্রতিষ্ঠার শুরু থেকেই বগুড়া লেডিস ক্লাব মৌলিক কার্যক্রম সমূহ এবং অর্পিত সকল দায়িত্ব সফলভাবে সম্পাদনের প্রচেষ্টা অব্যাহত রেখেছে। এবছর মুজিব শতবর্ষ উপলক্ষে বগুড়া লেডিস ক্লাবের তত্ত্বাবধানে একটি প্রামাণ্য চিত্র জ্ঞএঅজঐগঅ: অ ঈঅঞঋএঝউছঞ্চড় ঝঅকউজ্ঞ প্রদর্শনী এর আয়োজন করা হয়েছে। করোনা মহামারী চলমান সময়ে যখন সমগ্র বিশ্ব আতঙ্কিত এবং জর্জরিত, তখনও বগুড়া লেডিস ক্লাব মানুষের মাঝে খাদ্য সামগ্রী বিতরণ এর মাধ্যমে সাহায্যের হাত বাড়িয়ে দিয়েছে।<br /><br /></div>\r\n<div style=\"text-align: justify;\">এই সংগঠনের গঠনতন্ত্র অনুযায়ী সকল সদস্যাগন তাদের দায়িত্বসমূহ সঠিকভাবে পালন করছেন।&nbsp; জুনিয়র সদস্যাগণ বিভিন্ন কার্যক্রমে সিনিয়র সদস্যাগণের পরামর্শ গ্রহণ করেন এবং সিনিয়র ভাবীগণ তাদের নিজ অভিঞ্চতার আলোকে সঠিক দিকনির্দেশনা প্রদান করে থাকেন যা আমাদের সামাজিক মূল্যবোধ চর্চার অন্যতম ভিত্তি। এই সংগঠনটি আমাদের পরিবারিক ও সেনাজীবনের পারস্পরিক সৌহার্দ্য, সম্প্রীতি, আন্তরিকতা ও দেশপ্রেম বৃদ্ধি করতে শেখায় যা আমাদের এই সুশৃঙ্খল জীবনযাপনকে সহজতর ও আনন্দময় করে। সকলের আত্মপ্রত্যয় ও সম্মিলিত প্রচেষ্টায় এই সংগঠনটি তার সঠিক লক্ষ্যে অগ্রসর হবে এই আমার দৃঢ় বিশ্বাস।<br /><br /></div>\r\n<div style=\"text-align: justify;\">পরিশেষে লেডিস ক্লাবের সকল সদস্যকে ধন্যবাদ জানিয়ে এবং মহান সৃষ্টিকর্তার কাছে সকলের সুস্বাস্থ্য ও মঙ্গল কামনা করে শেষ করছি। আল্লাহ হাফেজ।</div>', NULL, 'মিসেস মনোয়ারা নাজমুল', NULL, NULL);
INSERT INTO `sobanetries` VALUES (170, 2, '-2023-08-29-64ee0091075e6.jpeg', 1, '2021-01-07 04:43:16', '2023-08-29 14:41:12', '২০ অগাস্ট ২০২৩', '', 8, 2, 3, NULL, '<p style=\"text-align: justify;\">চট্টগ্রাম এরিয়া লেডিস ক্লাব বাংলাদেশ সেনাবাহিনীর একটি গুরুত্বপূর্ণ এবং সুপ্রতিষ্ঠিত সংগঠন। সূচনালগ্ন থেকেই চ-গ্রাম এরিয়া লেডিস ক্লাব তার সাফল্যের পদস্মারক রেখে আসছে। এই সংগঠনটি এর সদস্যাদের মধ্যে সৌহার্দ্য বৃদ্ধি করা ছাড়াও দেশের আর্থ-সামাজিক উন্নয়নে ভূমিকা রাখার জন্য উদ্বুদ্ধ করে থাকে। সদস্যাদের মাঝে ভ্রাতৃত্ববোধ বৃদ্ধির পাশাপাশি কৃষ্টি ও সংস্কৃতির বিকাশে এই সংগঠন অনন্য। আজ তথ্য প্রযুক্তির কঠিন শেকলে যখন সবাই বন্দি, তখন এই সংগঠন যেন শেকল থেকে মুক্ত হবার সোনার কাঠি।</p>\r\n<p style=\"text-align: justify;\"><br />চ-গ্রাম এরিয়া লেডিস ক্লাব প্রতি বছর বিভিন্ন সামাজিক অনুষ্ঠান যেমন মেহেদি উৎসব, পহেলা বৈশাখে বাংলা নতুন বর্ষবরণ, পিঠা উৎসবসহ অন্যান্য সকল অনুষ্ঠান অত্যন্ত সফলতার সাথে সম্পন্ন করে থাকে। বিশ্বব্যাপী কোভিড-১৯ পরিস্থিতির কারণে চ-গ্রাম এরিয়া লেডিস ক্লাবের দৈনন্দিন কার্যক্রম সাময়িক ভাবে ব্যাহত হলেও সকলের আন্তরিকতা, ভালোবাসা আর সহযোগিতার ফলে &nbsp;চ-গ্রাম এরিয়া লেডিস ক্লাবের অন্যান্য সকল অনুষ্ঠান সুষ্ঠু ও সুন্দরভাবে সম্পন্ন হয়েছে। এছাড়াও খাগড়াছড়ি, বান্দরবন, রাঙামাটি এবং গুইমারা শাখা লেডিস ক্লাবসমূহ তাদের সকল অনুষ্ঠান এবং কার্যাবলী সুষ্ঠ ও সুন্দরভাবে সম্পন্নের ফলে চট্টগ্রাম এরিয়া লেডিস ক্লাবের উন্নতি ও সাফল্যের মান অক্ষুন্ন রেখেছে। &nbsp;</p>\r\n<p style=\"text-align: justify;\"><br />চট্টগ্রাম এরিয়া লেডিস ক্লাবের প্রধান পৃষ্ঠপোষক বেগম দিলশাদ নাহার আজিজের দিকনির্দেশনা এবং নেতৃত্বে ক্লাবের সাংগঠনিক কাঠামো এবং আনুষ্ঠানিক কার্যাবলীতে উত্তরোত্তর উন্নতি সাধিত হয়েছে। পাশাপাশি চট্টগ্রাম এরিয়া লেডিস ক্লাবের বিগত সকল সভানেত্রীদের নিরলস প্রচেষ্টা এবং নেতৃত্বে সংগঠনটি এতদূর এগিয়ে এসেছে। এছাড়াও সকল সহ-সভানেত্রী, সাধারন সম্পাদিকা, কোষাধ্যক্ষা এবং সকল সদস্যাদের অকৃত্রিম ভালোবাসা &nbsp;এবং একনিষ্ঠতায় চট্টগ্রাম এরিয়া লেডিস ক্লাব আজ সুপ্রতিষ্ঠিত এবং গৌরবজ্জল একটি সংগঠন।&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;<br />বর্তমান পরিস্থিতিতে আমাদের প্রার্থনা, যেন পৃথিবীতে স্বাভাবিক এবং সুস্থ পরিবেশ ফিরে আসে এবং সকলে তাদের দৈনন্দিন কার্যাবলীতে সুষ্ঠভাবে ফেরত যেতে পারে। চট্টগ্রাম এরিয়া লেডিস ক্লাবের আঙিনা যেন আবার মুখরিত হয় এবং যেন আমরা সকলে এই ক্লাবের উত্তরোত্তর সমৃদ্ধিতে সক্রিয়ভাবে অংশগ্রহন করতে পারি। পরম করুনাময় আল্লাহ তাআলা আমাদের সকলের সহায় হউন। আমিন।&nbsp;</p>', NULL, 'ফাতেমা তুজ জোহরা', NULL, NULL);
INSERT INTO `sobanetries` VALUES (173, 3, '-2024-11-03-67271cb85d327.jpeg', 1, '2021-01-07 04:49:34', '2024-11-03 07:12:03', '২৪ অক্টোবর ২০২৪', '', 2, 3, 3, NULL, '<p>চিলড্রেন ক্লাব সেনা অফিসার পরিবারের কোমলমতি শিশুদের মনোবিকাশের লক্ষ্যে প্রতিষ্ঠিত একটি বিশেষ শিক্ষা প্রতিষ্ঠান। এ প্রতিষ্ঠানে শিশুদের সহপাঠ্যক্রম বা Extra Curricular Activities এর উপর গুরুত্ব আরোপ করে বিভিন্ন ধরনের ক্লাস পরিচালনা করা হয়। শেখ হাসিনা সেনানিবাসে কর্মরত অফিসারগণের শিশু সন্তানদের মনস্তাত্ত্বিক বিকাশ, সামাজিক সম্পৃক্ততা বৃদ্ধি এবং সাংস্কৃতিক অঙ্গনে সুপ্ত প্রতিভা বিকাশের লক্ষ্যে গত ২০ জানুয়ারি ২০১৯ তারিখে চিলড্রেন ক্লাব, বরিশাল অঞ্চল প্রতিষ্ঠা করা হয়। অপরূপা পায়রা নদীর নৈসর্গিক সৌন্দর্য ও ভৌগলিক অবস্থানের উপর ভিত্তি করে অত্র চিলড্রেন ক্লাবের নামকরণ করা হয় &rdquo;<strong>রিভারসাইড চিলড্রেন ক্লাব</strong>&rsquo;&rsquo;।</p>\r\n<p>শিশুরাই আমাদের জাতির ভবিষ্যৎ। হাটি হাটি পা পা করে গড়ে উঠা রিভারসাইড চিলড্রেন ক্লাব আজ একটি কার্যকরী প্রতিষ্ঠান ও শিশুদের মনোবিকাশের সূতিকাগারে পরিণত হয়েছে। ক্লাবটি উৎসাহ ও আনন্দমুখোর পরিবেশ তৈরির মাধ্যমে ছোট্টসোনামণিদের সামনের দিকে এগিয়ে যেতে সহযোগিতা করছে। আমি আশা করি, শৈশবের এই প্রারম্ভিক পর্যায়ে উৎসাহ ব্যঞ্জক বিচিত্র শিক্ষা ও অভিজ্ঞতা আমাদের শিশুদের ভবিষ্যৎ জীবন গড়ার ক্ষেত্রে পাথেয় হয়ে থাকবে।</p>\r\n<p>পরিশেষে রিভারসাইড চিলড্রেন ক্লাব, বরিশাল অঞ্চেলের উত্তরোত্তর উন্নতি, সমৃদ্ধি ও সর্বাঙ্গীন মঙ্গল কামনা করছি। মহান আল্লাহ তায়ালা আমাদের সকলের সহায় হউন।</p>', NULL, 'মাহফুজা রহমান', NULL, NULL);
INSERT INTO `sobanetries` VALUES (174, 3, '-2024-11-17-673952dac5798.jpeg', 1, '2021-01-07 04:51:05', '2024-11-17 02:20:11', '২৩ অক্টোবর ২০২৪', '', 4, 3, 3, NULL, '<p>&nbsp;</p>\r\n<p>রামু সেনানিবাসে কর্মরত অফিসারগণের কোমলমতি শিশুদের মাঝে পারষ্পরিক বন্ধুত্ব সুলভ সম্পর্ক সৃষ্টি, মানসিক বিকাশ, সহযোগিতা, সহমর্মিতা এবং শিশুদের আত্মবিশ্বাসী করে গড়ে তুলতে চিলড্রেন ক্লাব, কক্সবাজার অঞ্চল প্রতিষ্ঠা করা হয়। শিশুদের সুপ্ত প্রতিভা বিকাশে চিলড্রেন ক্লাবের ভূমিকা অপরিসীম। &nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\">সাউদার্ন শীল্ড পরিবারে রামু ক্যান্টনমেন্টে গত ২৮ অক্টোবর ২০১৫ সালে চিলড্রেন ক্লাব, কক্সবাজার অঞ্চল প্রতিষ্ঠা লাভ করে। পরবর্তীতে আনুষ্ঠানিকভাবে ২০১৬ সালে চিলড্রেন ক্লাবের কার্যক্রম পূর্ণরুপে চালু করা হয়। প্রতিষ্ঠার পর থেকে ১০ পদাতিক ডিভিশনের বিভিন্ন সাংষ্কৃতিক অনুষ্ঠান এবং বিশেষ দিবস সমূহে চিলড্রেন ক্লাব সক্রিয়ভাবে অংশগ্রহন করে আসছে। পূর্বে চিলড্রেন ক্লাবের কোন নিজস্ব কমপ্লেক্স না থাকায় ক্লাবের সকল কার্যক্রম লেডিস ক্লাবে সম্পন্ন করা হতো। পরবর্তীতে দুটি এমওকিউ সংষ্কার করে চিলড্রেন ক্লাবের নব স্থাপনা তৈরী করা হয়। ১৭ মার্চ ২০২০ তারিখে চিলড্রেন ক্লাব, কক্সবাজার অঞ্চল এর নব&nbsp; স্থাপনা \"নতুন কুঁড়ি\" এর শুভ উদ্বোধন করা হয়।</p>\r\n<p style=\"text-align: justify;\">সেনাবাহিনীতে কর্মরত অফিসারগণের সন্তানের মাঝে সুস্থ্য সাংষ্কৃতিক চর্চা বিকাশের লক্ষ্যে চিলড্রেন ক্লাব, কক্সবাজার অঞ্চলে নাচ, গান, অংকন এবং আরবি শিক্ষা ইত্যাদি প্রশিক্ষণ প্রদান করা হয়। এর মাধ্যমে তাদেরকে আত্মবিশ্বাসী এবং দৃঢ় মনোবলের অধিকারী হিসেবে গড়ে তুলতে চিলড্রেন ক্লাব বিশেষ ভূমিকা পালন করছে।</p>\r\n<p style=\"text-align: justify;\">প্রতিষ্ঠার পর থেকেই চিলড্রেন ক্লাব, কক্সবাজার অঞ্চল সকল কার্যক্রম সুষ্ঠুভাবে পালন করছে। চিলড্রেন ক্লাব, কক্সবাজার অঞ্চল বিভিন্ন সহশিক্ষা কার্যক্রম পরিচালনার মাধ্যমে আজকের কোমলমতি শিশুদেরকে ভবিষ্যতের আলোকিত মানুষ হিসেবে গড়ে তুলতে দৃঢ় প্রত্যয়ী।&nbsp;</p>', NULL, 'সৈয়দা মার্জিয়া বেগম', NULL, NULL);
