INSERT INTO `sobanetries` VALUES (205, 2, '-2021-07-29-61021b58ed542.jpg', 1, '2021-01-18 02:35:39', '2022-03-24 03:05:33', '১৪ জুলাই ২০২১', '', 4, 2, 3, NULL, '<div style=\"text-align: justify;\">রামু ক্যান্টনমেন্টে ২৮ অক্টোবর ২০১৫ তারিখে যাত্রা শুরু করে লেডিস ক্লাব কক্সবাজার অঞ্চল। সেনাবাহিনীর পরিমন্ডলে পারস্পারিক সৌহার্দ্যপূর্ণ পরিবেশ ও সহমর্মিতা গড়ে তোলা, মূল্যবোধ, শ্রদ্ধা এবং আন্তরিকতা বৃদ্ধি করে পারিবারিক বন্ধন সূদৃঢ় করার ভিত্তি স্থাপনা করাই এ প্রতিষ্ঠানের প্রধান উদ্দেশ্য। সেনাবাহিনীর চেতনা, নিয়ম-শৃংখলা ও ক্লাবের সদস্যদের মাঝে সুস্থ্য বিনোদনমূলক পরিবেশ সৃষ্টি করে সকল সদস্যাদের সেনাবাহিনীর আলোকে গড়ে তোলাই আমাদের লক্ষ্য।</div>\r\n<div style=\"text-align: justify;\">স্বনামধন্য লেডিস ক্লাব কক্সবাজার অঞ্চলের সভানেত্রী হতে পেরে আমি অত্যন্ত আনন্দিত। লেডিস ক্লাব কক্সবাজার অঞ্চল মাননীয় প্রধান পৃষ্ঠপোষক এর নির্দেশনা অনুযায়ী যেন সবসময় অক্লান্ত পরিশ্রম করে বাংলাদেশ সেনাবাহিনীর গৌরব বৃদ্ধি করতে পারে।&nbsp;</div>\r\n<div style=\"text-align: justify;\">পরিশেষে মুজিববর্ষের লগ্নে সকলের জন্য রইল আমার শুভেচ্ছা। এ প্রতিষ্ঠানের সাথে সংশ্লিষ্ট সকলের মেধা, শ্রম ও নিষ্ঠার জন্য আন্তরিক ধন্যবাদ এবং করোনার প্রাদুর্ভাব হতে সকলের সুসহতা কামনা করছি। মহান আল্লাহ তাআলা আমাদের সকলকে হেফাজতে রাখুন।</div>', NULL, 'রাশিদা আহসান', NULL, NULL);
