INSERT INTO `sobanetries` VALUES (216, 2, '-2021-06-28-60d9e6e7eadc2.jpg', 1, '2021-01-18 07:51:09', '2021-06-28 15:13:47', '০৭ জানুয়ারী ২০২১', '', 7, 2, 3, NULL, '<p>লেডিসক্লাব, ঘাটাইল অঞ্চলের সভানেত্রীর দায়িত্ব পালন করতে পেরে আমি অত্যন্ত আনন্দিত। লেডিস ক্লাব, ঘাটাইল অঞ্চলের সাথে সম্পৃক্ত সকলকে আমার পক্ষ থেকে জানাই আন্তরিক শুভেচ্ছা।</p>\r\n<div style=\"text-align: justify;\">ঘাটাইল লেডিস ক্লাব এই অঞ্চলের একটি গুরুত্বপূণ&Ntilde; সামাজিক সংগঠন। সম্মানিত পৃষ্ঠপোষক দিলশাদ নাহার আজিজ এর সার্বিক নির্দেশনা ও সুদূর প্রসারী চিন্তা-চেতনার আলোকে ঘাটাইল লেডিস ক্লাব তার কায&Ntilde;ক্রম পরিচালনা করে আসছে। অফিসার সহধর্মিনীদের নিয়ে পরিচালিত এই সংগঠন পারষ্পারিক সৌহাদ্য&Ntilde;, আন্তরিকতা ও সামাজিকতার অনুশীলনের মাধ্যমে দেশের আথ&Ntilde;-সামাজিক উন্নয়নে ভূমিকা রাখতে সকল সদস্যাদের উদ্বুদ্ধ করে থাকে।</div>\r\n<div style=\"text-align: justify;\">সুস্থ বিনোদনের সাংস্কৃতিক চর্চা, পারষ্পারিক মত বিনিময় এবং শারীরিক-মানসিক স্বাস্থ্য সংক্রান্ত বিভিন্ন ক্লাস পরিচালনার মাধ্যমে সুন্দরভাবে জীবন যাপন এবং পারিবারিক-সামাজিক দায়িত্ববোধের চেতনা ক্রমশঃ উন্নত করাই এই ক্লাবের মূল উদ্দেশ্য।</div>\r\n<div style=\"text-align: justify;\">সূচনা লগ্ন হতেই ঘাটাইল অঞ্চলের লেডিস ক্লাব সকল সদস্যাদের কল্যাণ, প্রগতি, আত্মনিভ&Ntilde;শীলতার ধারা অব্যহত রাখতে সদা সচেষ্ট। এই লেডিস ক্লাব এই অঞ্চলের সদস্যাদেরকে সেনাবাহিনীর নিয়মশৃংখলা, আচার-আচরণ, ঐতিহ্য তথা সহধর্মিনীর দায়িত্ব সম্পর্কে সচেতন করতে গুরুত্বপূণ&Ntilde; ভূমিকা পালন করে আসছে। সুস্থ বিনোদনের সাংস্কৃতিক চর্চার মাধ্যমে এবং বিভিন্ন প্রয়োজনীয় স্টাডি পিরিয়ড এর মাধ্যমে সকল সদস্যাদের মাঝে পারিবারিক ও সামাজিক মূল্যবোধ গড়ে তুলতে বিশেষ ভুমিকা পালন করে আসছে। এই লেডিস ক্লাব করোনাকালীন মহামারীতে ঘাটাইল অঞ্চলের দুস্থ, অসহায় ও দরিদ্র জন সাধারনের মধ্যে ত্রাণ সহায়তা ও স্বাস্থ্যসেবা প্রদানসহ বিভিন্ন কল্যাণমূলক কায&Ntilde;ক্রম গ্রহণ করে থাকে। মহামারীর এই ক্রান্তি লগ্নে লেডিস ক্লাব, ঘাটাইল অঞ্চল অসহায় ও অবহেলিত মানুষের পাশে দাঁড়াতে পেরে আমরা সকলে গর্বিত।</div>\r\n<div style=\"text-align: justify;\">পরিশেষে ঘাটাইল অঞ্চলের সকল সদস্যাকে জানাই আমার আন্তরিক শুভেচ্ছা ও অভিনন্দন। সেনা পরিবারের সঠিক কায&Ntilde;ক্রমকে কল্যাণকর, গতিশীল ও যুগোপযোগী করতে ঘাটাইল লেডিস ক্লাব সব&Ntilde;দা অগ্রণী ভূমিকা পালন করবে এই প্রত্যাশা করছি।</div>', NULL, 'বিলকিস বেগম সিমি', NULL, NULL);
