INSERT INTO `sobanetries` VALUES (227, 3, '-2024-11-03-672747867c778.jpeg', 1, '2021-01-18 10:20:25', '2024-11-03 09:51:02', '১৮ অগাস্ট ২০২৪', '', 12, 3, 3, NULL, '<p style=\"text-align: center; line-height: 1;\"><span style=\"font-size: 14pt;\">&nbsp;</span></p>\r\n<h1 style=\"text-align: center; line-height: 1;\"><span style=\"font-size: 14pt;\">\"</span><span style=\"font-size: 14pt;\">তোমাদের গানে, কল-কলতানে উছসি উঠিবে নদী</span></h1>\r\n<p style=\"text-align: center; line-height: 1;\"><span style=\"font-size: 14pt;\"> সরস করিয়া তৃণ ও তরুরে বহিবে সে নিরবধি</span></p>\r\n<p style=\"text-align: center; line-height: 1;\"><span style=\"font-size: 14pt;\">তোমরা আনিবে ফুল ও ফসল পাখি-ডাকা রাঙা ভোর</span></p>\r\n<p style=\"text-align: center; line-height: 1;\"><span style=\"font-size: 14pt;\">জগৎ করিবে মধুময়, প্রাণে প্রাণে বাঁধি প্রীতিডোর।</span><span style=\"font-size: 14pt;\">\"</span></p>\r\n<p style=\"text-align: center; line-height: 1;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">কবি সুফিয়া কামাল এর সাথে কণ্ঠ মিলিয়ে বলতে চাই আজকের শিশুরাই দেশ, সমাজ, জাতির ভবিষ্যৎ, ভবিষ্যতের কর্ণধার। শিশুদের সুষ্ঠু বিকাশের ক্ষেত্রে শারীরিক ও মানসিক&nbsp; উভয় দিকের প্রতি সমান গুরুত্ব দেয়া প্রয়োজন। এই উদ্দেশ্যকে সামনে রেখে,&nbsp; সেনাবাহিনীতে কর্মরত অফিসারগণের শিশু সন্তানদের জন্য চিলড্রেন ক্লাব প্রতিষ্ঠা করা হয়। চিলড্রেন ক্লাব, বিএমএ ১৯৮৯ সালের ২১ জুন তারিখে প্রতিষ্ঠা লাভ করে। অত্র সংগঠনটি প্রতিষ্ঠালগ্ন হতে ০৪ জুন ২০১৩ তারিখ পর্যন্ত চট্টগ্রাম অঞ্চলের শাখা হিসেবে সকল কার্যক্রম সম্পন্ন করেছে। গত ০৫ জুন ২০১৩ তারিখ হতে চিলড্রেন ক্লাব, বিএমএ পৃথক অঞ্চল হিসেবে নিজস্ব কর্মকান্ড পরিচালনা করছে। সূচনালগ্ন হতে বিএমএ চিলড্রেন ক্লাব অত্র একাডেমিতে কর্মরত অফিসারগণের শিশুদের মেধা, মননশীলতা এবং সাংস্কৃতিক অঙ্গনে সুপ্ত প্রতিভা বিকাশের লক্ষ্যে নাচ, গান, চিত্রাংকন, ভাষা শিক্ষা এবং যুগোপযোগী অন্যান্য বিষয়ের উপর প্রশিক্ষন কার্যক্রম পরিচালনা করে আসছে।</span></p>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">শিশুর মানসিক বিকাশ, শিশুদের মধ্যে বন্ধুত্বসুলভ সম্পর্ক সৃষ্টি, সুপ্ত প্রতিভার বিকাশ, সহমর্মিতা ও সহযোগিতার মাধ্যমে সুস্থ প্রতিযোগিতার মনোভাব গড়ে তোলা এবং পড়াশোনার একঘেয়েমি দূর করার উদ্দেশ্যে অত্র সংগঠনটি নিরলসভাবে কাজ করে যাচ্ছে। এছাড়াও স্বাধীনতা দিবস, জাতীয় শিশু দিবস, জাতীয় শোক দিবস, পহেলা বৈশাখ এবং বিভিন্ন গুরুত্বপূর্ণ দিবসে চিত্রাংকন প্রতিযোগিতা, সাংস্কৃতিক অনুষ্ঠান ও অন্যান্য অনুষ্ঠানাদি আয়োজনের মাধ্যমে চিলড্রেন ক্লাব, বিএমএ অঞ্চল গুরুত্বপূর্ণ ভুমিকা পালন করছে।</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">চিলড্রেন ক্লাব, বিএমএ অঞ্চলের সভানেত্রী হিসেবে মহান আল্লাহ তায়ালার নিকট প্রার্থনা করি, আমরা যেন এই প্রতিষ্ঠানের অগ্রগতির ধারাকে অক্ষুন্ন রেখে আমাদের ভবিষ্যত প্রজন্মকে সুস্থ মানসিক বিকাশের মাধ্যমে গড়ে তুলতে পারি। পরিশেষে আমি চিলড্রেন ক্লাব, বিএমএ অঞ্চলের&nbsp; উত্তরোত্তর উন্নতি, অগ্রগতি ও সমৃদ্ধি কামনা করছি।</span></div>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', NULL, 'নাজমুন নাহার', NULL, NULL);
