INSERT INTO `sobanetries` VALUES (232, 3, '-2023-08-08-64d24b51c5dcc.jpeg', 1, '2021-01-19 01:28:24', '2023-08-27 11:50:41', '০৫ সেপ্টেম্বর ২০২২', '', 5, 3, 3, NULL, '<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">বগুড়া অঞ্চলের \"চিলড্রেন ক্লাব\'\' সেনাবাহিনীর একটি ঐতিহ্যবাহী আঞ্চলিক সংগঠন, যা গত ০৮ এপ্রিল ১৯৮৪ সাল থেকে এর যাত্রা শুরু করে। চিলড্রেন ক্লাবের বিভিন্ন গঠনমূলক প্রশিক্ষণ, সুস্হ সাংস্কৃতি চর্চা, সামাজিক বন্ধন সুদৃঢ় করাসহ সার্বিকভাবে পরিপূর্ণ মানসিক বিকাশ সাধনের জন্য বিভিন্ন কর্মকান্ড পরিচালনা করার মাধ্যমে শিশুদের প্রতিভা বিকাশের সুযোগ সৃষ্টি করা হয়েছে। সুস্হ সাংস্কৃতিক চর্চা বিকাশের লক্ষ্যে নাচ, গিটার, গান এবং অঙ্কন ইত্যাদি প্রশিক্ষণের মাধ্যমে তাদের আত্মবিশ্বাসী ও দৃঢ় মনোবলের অধিকারী হিসাবে গড়ে তোলার প্রচেষ্টা অব্যাহত আছে। চিলড্রেন ক্লাবের বহুমুখী কর্মকান্ড এই ক্লাবের সদস্যদের মাঝে পারস্পরিক সৌহার্দ্য বৃদ্ধি এবং সুস্হ সামাজিকতা বিকাশের অভাবনীয় অবদান রেখে চলেছে।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">বগুড়া অঞ্চলের \"চিলড্রেন ক্লাব\'\' শিক্ষার্থীদের মেধা ও উৎকর্ষতা বিকাশের উদ্দেশ্যে প্রশিক্ষণের পাশাপাশি যে কোন ঐতিহ্যবাহী দিবসে যেমন, \"মুজিব শতবর্ষ\'\' উপলক্ষে চিত্রাংকন প্রতিযোগীতা, বাৎসরিক বনভোজন এবং সাংস্কৃতিক অনুষ্ঠান আয়োজনের মাধ্যমে সুপ্ত প্রতিভা বিকাশ, মেধা মননশীলতা যাচাই ও উৎসাহ প্রদানে এক অনন্য সুযোগ সৃষ্টি করতে সক্ষম হয়েছে।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">আমি আশা করি বিগত বছরের ন্যায় আগামী দিন গুলোতে আমাদের চিলড্রেন ক্লাব আরও যুগোপযোগী ও গতিশীল কার্যক্রমের মাধ্যমে শিশুদের প্রকৃত কল্যাণে অধিকতর অবদান রাখবে।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">পরিশেষে, চিলড্রেন ক্লাব এর সকল সদস্যদের এবং শিক্ষার্থীদের জীবন সুন্দর ও আনন্দময় হউক। চিলড্রেন ক্লাব বগুড়া অঞ্চলের উত্তরোত্তর উন্নতি, অগ্রগতি ও সমৃদ্ধি লাভ করবে এই প্রত্যাশা করছি। সকলের জন্য রইল শুভকামনা।</span></p>', NULL, 'মিসেস নাহিদ শারমিন', NULL, NULL);
