INSERT INTO `sobanetries` VALUES (261, 1, '-2024-11-08-672e23b3ca888.jpeg', 1, '2021-01-19 06:36:42', '2024-11-08 14:54:31', '১৮ অক্টোবর ২০২৪', '', 9, 1, 3, NULL, '<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">প্রাকৃতিক সৌন্দর্যে সুশোভিত ময়নামতির স্মৃতি বিজড়িত কুমিল্লা সেনানিবাসের নৈসর্গিক পরিবেশে অবস্থান করছে সেনা পরিবার কল্যাণ সমিতি কুমিল্লা অঞ্চল। কুমিল্লা সেনানিবাসের মনোরম ও নান্দনিক পরিবেশে অবস্থিত এ অঞ্চলের রয়েছে সুদীর্ঘ ৪৩ বছরের গৌরবোজ্জল সাফল্যের ইতিহাস।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">সেনা পরিবার কল্যাণ সমিতি (সেপকস) কুমিল্লা অঞ্চল ১৯৭৮ সালে প্রতিষ্ঠা লাভ করে। নারী উন্নয়ন ও কল্যাণমূখী সামজিক সংগঠন হিসেবে এ অঞ্চল ইতোমধ্যে বাংলাদেশ সেনাবাহিনীতে গৌরবময় অবস্থান প্রতিষ্ঠা করেত সক্ষম হয়েছে। বাংলাদেশ সেনাবাহিনীতে কর্মরত জুনিয়র কমিশন্ড অফিসার, নন কমিশন্ড অফিসার, অন্যান্য পদবীর সৈনিক ও বেসামরিক কর্মচারীদের পরিবারবর্গের সামাজিক, সাংস্কৃতিক, শিক্ষা, অর্থনৈতিক এবং অন্যান্য কল্যাণমূলক কার্যাবলী উন্নয়নের লক্ষ্যে সেনা পরিবার কল্যাণ সমিতি, কুমিল্লা অঞ্চল কাজ করে যাচ্ছে।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">বিশ্বায়নের এই যুগে সেনা পরিবারবর্গের সদস্যাদের স্বাবলম্বী, আত্মপ্রত্যয়ী, কর্মদক্ষ এবং স্বনির্ভর করে গড়ে তোলার লক্ষে এ অঞ্চলে প্রতি বছর গড়ে ৭০ থেকে ৮০ জন সদস্যা বিভিন্ন কোর্সের মাধ্যমে প্রশিক্ষিত করা হচ্ছে। দেশের আর্থ-সামাজিক উন্নয়নের অংশীদার হিসেবে বৈশ্বিক চ্যালেঞ্জ মোকাবেলায় দক্ষ মানব সম্পদ তৈরিতে এ অঞ্চল বরাবরের মতোই অসামান্য অবদান রেখে চলেছে । নিজ সদস্যাদের উন্নয়নের পাশাপাশি, সংস্থাটি বিভিন্ন ক্ষেত্রে আর্তমানবতার সেবায় উল্লেখযোগ্য ভূমিকা রেখে আসছে। বৈশ্বিক মহামারী কোভিড-১৯&nbsp; করোনা ভাইরাসের সংকটময় মুহূর্তে সংস্থাটির বিশেষ উদ্যোগ ও ত্রাণ বিতরণ সহ বিভিন্ন প্রচেষ্টা ছিল এরই এক উজ্জ্বল দৃষ্টান্ত।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">আজকের শিশু-কিশোররাই আগামী দিনের সম্পদ। তাদের ভিতর লুকিয়ে থাকা সুপ্ত প্রতিভার সঠিক বিকাশের কথা বিবেচনায় রেখে সেপকস কুমিল্লা অঞ্চল প্রশিক্ষণের পাশাপাশি সেনা সদস্যদের সন্তানদের জন্য কলকন্ঠ ক্লাব পরিচালনা করে যাচ্ছে। আমি দৃঢ়ভাবে বিশ্বাস করি এই প্রতিষ্ঠানের মাধ্যমে কুমিল্লা সেনানিবাসের সকল সেনাসদস্যদের সন্তানদের প্রাতিষ্ঠানিক শিক্ষার পাশাপাশি সামাজিক ও সাংস্কৃতিক শিক্ষা প্রদানেও বিশেষ অবদান রেখে যাচ্ছে। আমি নিঃসন্দেহে বলতে চাই কলকন্ঠ ক্লাবের শিক্ষার্থীদের অর্জিত জ্ঞান বর্তমান প্রতিযোগিতাপূর্ণ বিশ্বে নিজেদের অবস্থান আরও বেশি সুদৃঢ় ও আত্মবিশ্বাসে বিলয়ান করবে। এ অঞ্চলের শিশুদের সুপ্ত প্রতিভা আরো বেশী বিকশিত হোক এটিই আমার একান্ত কামনা।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">নারীর উন্নয়নমূলক কার্যক্রমের পাশাপাশি জাতীয় জীবনেও এ অঞ্চল গুরুত্বপূর্ণ ভূমিকা পালন করে যাচ্ছে। এ অঞ্চলের নারীরা তাদের নিজ নিজ ক্ষেত্রে গুরুত্বপূর্ণ অবদানের মাধ্যমে সেনা পরিবার কল্যাণ সমিতিকে এক নতুন উচ্চতায় নিয়ে যাবে এটাই আমার প্রত্যাশা। বিবর্তনের খেয়ায় সেই সমুজ্জ্বল ইতিহাসের পিছনে রয়েছে অসংখ্য সদস্যা, শিক্ষক-শিক্ষিকা, নিয়োজিত স্টাফবৃন্দের ত্যাগ ও তিতিক্ষা। এই পর্যায়ে আমি শ্রদ্ধাভরে স্মরণ করতে চাই সেপকস কুমিল্লা অঞ্চলের প্রাক্তন সকল সদস্যাদের যাদের অক্লান্ত শ্রম, মেধা ও ত্যাগের বিনিময়ে বহমান সুদীর্ঘ ৪৩ বছরের গৌরবোজ্জ্বল সাফল্যের ইতিহাস এবং আজকের এই অবস্থান।</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">পরিশেষে, সেনা পরিবার কল্যাণ সমিতি কুমিল্লা অঞ্চলের সবাইকে জানাই আন্তরিক শুভেচ্ছা ও অভিনন্দন। এ অঞ্চলের অগ্রগতি ও উন্নতির ধারা অব্যাহত থাকুক এই কামনাই করছি। মহান আল্লাহ তায়ালা আমাদের সকলের সহায় হউন। আমিন।</span></p>', NULL, 'সায়মা খান', NULL, NULL);
