INSERT INTO `sobanetries` VALUES (288, 3, '-2023-08-07-64d07a4e7ca2c.jpeg', 1, '2021-01-20 04:59:30', '2023-08-07 04:59:58', '০৭ মার্চ ২০২২', '', 1, 3, 3, NULL, '<p style=\"text-align: justify;\">চিলড্রেন্স ক্লাব, ঢাকা অঞ্চল তার সমৃদ্ধ ঐতিহ্যের ধারাবাহিকতায় মুজিববর্ষ এবং মহান স্বাধীনতার সূবর্ণ জয়ন্তীর চেতনাকে ধারণ করে নিজস্ব স্বকীয়তায় এই বছরও সামগ্রিক কার্যক্রম পরিচালনা করতে যাচ্ছে জেনে আমি আনন্দিত।</p>\r\n<p style=\"text-align: justify;\">ঢাকা সেনানিবাসে কর্মরত সেনা অফিসারদের শিশু সন্তানদের সংস্কৃতি অঙ্গনে সুপ্ত প্রতিভা বিকাশের লক্ষ্যে গত ০৩ জুন ২০০১ তারিখ থেকে চিলড্রেন্স ক্লাব, ঢাকা অঞ্চল শুভ যাত্রা শুরু করে। প্রতিষ্ঠানিক শিক্ষার পাশাপাশি সাহিত্য-সংস্কৃতির উপযুক্ত অনুশীলন শিশুদের মধ্যে সহমর্মিতা ও সুস্থ প্রতিযোগিতার ক্ষেত্র তৈরী করে। কোমলমতি শিশুদের সামাজিক মূল্যবোধ এর অনুপ্রেরণায় নিজ মেধা-মননের পরিস্ফুটনে এই প্রতিষ্ঠান তার মহতী উদ্যোগে শিশুদের আদর্শ মানুষ হিসেবে গড়ে তুলতে সহায়তা করছে।&nbsp;</p>\r\n<p style=\"text-align: justify;\">জাতির পিতা বঙ্গবন্ধু শেখ মুজিবুর রহমানের স্বপ্নের সোনার বাংলা গড়ে তোলার লক্ষ্যে আজকের নতুর প্রজন্মকে যেমন আধুনিক বিজ্ঞান ও প্রযুক্তিতে দক্ষ হিসেবে গড়ে তুলতে হবে ঠিক তেমন ভাবে তাদের উদ্ধুদ্ধ হতে হবে দেশীয় শিল্প-সাহিত্য ও সংস্কৃতিচর্চায়। দেশীয় &nbsp;সাহিত্য ও সংস্কৃতিচর্চাই নতুন প্রজ&curren;&oelig;কে নিঃস্বার্থ দেশপ্রেমিক সুনাগরিক হিসেবে গড়ে তুলতে পারে। আর তখনই এই নতুন প্রজ&curren;&oelig; আধুনিক বাংলাদেশ গড়তে নিজেকে প্রস্তুত করতে পারবে।</p>\r\n<p style=\"text-align: justify;\">আমি অভিন্দন জানাচ্ছি চিলড্রেন্স ক্লাব, ঢাকা অঞ্চল এর সকল কোমলমতি শিশুদের যাদের স্ব-স্ব সাহিত্যকর্ম, চিত্রকর্ম ও ভিন্ন মাত্রার শৈল্পিক সৃষ্টি নতুন রুপে উম্মোচিত হতে যাচ্ছে। চিলড্রেন্স ক্লাব, ঢাকা অঞ্চল এর মাননীয় প্রধান পৃষ্ঠপোষক এর সার্বক্ষনিক দিক নির্দেশনা, উৎসাহ ও অনুপ্রেরনায় প্রতিষ্ঠানটির সার্বিক উন্নয়নের ধারা অব্যাহত রয়েছে। চিলড্রেন্স ক্লাব এর বিস্তারিত নীতিমালা তাঁরই চিন্তাপ্রসূত নির্দেশিকা। আমি মাননীয় প্রধান পৃষ্ঠপোষক এর প্রতি তার অসামান্য অবদানের জন্য আন্তরিক কৃতজ্ঞতা ও অভিনন্দন জ্ঞাপন করছি। একই সাথে অভিনন্দন জানাই প্রতিষ্ঠানটির সহ-সভানেত্রী, সচিব, কোষাধ্যক্ষ, সদস্যাবৃন্দ, সমন্বয়কারী অফিসার এবং অন্যান্য সকলকে যাদের নিঃস্বার্থ সেবা ও শ্রমে চিলড্রেন্স ক্লাব, ঢাকা অঞ্চল সমৃদ্ধি লাভ করেছে।&nbsp;</p>\r\n<p style=\"text-align: justify;\">পরিশেষে চিলড্রেন্স ক্লাব, ঢাকা অঞ্চলের উত্তরোত্তর সাফল্য কামনা করছি। পরম করুণাময় মহান আল্লাহ তায়ালা আমাদের সকলের সহায় হোন।</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', NULL, 'সামিয়া যুবায়ের', NULL, NULL);
