INSERT INTO `sobanetries` VALUES (308, 2, '-2024-11-03-672745febe4b7.jpg', 1, '2021-01-25 11:51:32', '2024-11-03 15:41:49', '২৭ অক্টোবর ২০২৪', '', 1, 2, 3, NULL, '<p style=\"text-align: justify;\">লেডিস ক্লাব বাংলাদেশ সেনাবাহিনীর একটি অত্যন্ত গুরুত্বপূর্ণ সংগঠন। সেনাবাহিনীর প্রতিষ্ঠা লগ্ন থেকেই লেডিস ক্লাব, এর &nbsp;সদস্যাদের মধ্যে সম্প্রীতি ও সৌহার্দ্য বৃ্দ্ধির সেতুবন্ধন হিসাবে কাজ করে আসছে । এই ক্লাব স্বল্প পরিসরে সেনাবাহিনী এবং সমাজের উন্নয়নের জন্য যে অবদান রাখছে তা সত্যিই প্রশংসনিয় । ঢাকা সেনানিবাস লেডিস ক্লাব সেনাবাহিনীর সর্ববৃহৎ লেডিস ক্লাব হিসেবে বিভিন্ন বাস্তবধর্মী প্রশিক্ষণ প্রদান,সাংস্কৃতিক অনুষ্ঠান,আলোচনা, সেমিনার প্রভৃতি আয়োজনের মধ্যদিয়ে সদস্যাদের সেনাবাহিনীর নিয়ম শৃঙ্গলা,মূ্ল্যবোধ ও ঐতিহ্য সম্পর্কে ধারনা প্রদান ছাড়াও অফিসার পত্নীদের সুপ্ত প্রতিভা বিকাশেও গুরুত্বপূর্ণ ভূমিকা পালন করে আসছে। এছাড়াও সামাজিক দায়বদ্ধতা থেকে উক্ত সংগঠনটি করোনা কালীন সময়ে অসহায় দরিদ্রদের মাঝে ত্রান বিতরণ, দুস্থদের মাঝে শীত বস্ত্র বিতরণ ইত্যাদি নানা মূখী কল্যাণকর কাজ করে থাকে। ভবিষ্যতে এ সংগঠন তার ব্যাপ্তি বৃদ্ধি করে অফিসার পত্নীদের মাঝে দৃঢ় বন্ধন স্থাপন করার পাশাপাশি জনহিতকর কাজকর্মে আরও সম্পৃক্ত হবে বলে আশাবাদ ব্যক্ত করছি । ঢাকা সেনানিবাস লেডিস ক্লাবের সর্বাঙ্গীণ উন্নতির জন্য সকলের আন্তরিক সহযোগিতা একান্ত কাম্য।</p>', NULL, 'রেহানা শামীম', NULL, NULL);
