INSERT INTO `sobanetries` VALUES (318, 3, '-2023-09-10-64fde32a143e2.jpeg', 1, '2021-01-27 07:21:23', '2023-09-10 15:39:22', '১৮ অগাস্ট ২০২৩', '', 3, 3, 3, NULL, '<p style=\"text-align: justify;\">&nbsp;আজকের শিশু আগামী দিনের ভবিষ্যৎ। তাই ভবিষ্যৎ সুখী সমৃদ্ধ সোনার বাংলা গড়তে শিশুদেরকে দক্ষ, যোগ্য ও সৃজনশীল নাগরিক হিসেবে গড়ে তোলা আমাদের নৈতিক দায়িত্ব। সেই দায়বদ্ধতা থেকেই সেনাবাহিনীতে কর্মরত কর্মকর্তাবৃন্দের সন্তানদের কল্যাণার্থে প্রতিটি সেনানিবাসেই গড়ে তোলা হয়েছে অত্যন্ত সুসজ্জিত ও সমৃদ্ধ চিলড্রেন ক্লাব। এরই ধারাবাহিকতায় বিগত ২০০০ সালে সাভার সেনানিবাসে প্রতিষ্ঠা করা হয় সাভার চিলড্রেন ক্লাব।</p>\r\n<p style=\"text-align: justify;\">সাভার সেনানিবাসের সবুজ প্রকৃতির মাঝে প্রতিষ্ঠিত সাভার চিলড্রেন ক্লাবটি সবসময়ই অত্যন্ত দায়িত্বশীলভাবে এখানকার শিশুদের জন্য নাচ, গান, অঙ্কন, আবৃত্তি, অভিনয় ও যন্ত্রসংগীত এর মত নানা ধরনের প্রশিক্ষণ পরিচালনা করে আসছে। এ সকল প্রশিক্ষণ আমাদের শিশুদের মধ্যে সুস্থ প্রতিযোগিতা, সৃজনশীল মেধা বিকাশ, সহযোগিতা ও সহমর্মিতামূলক মনোভাব সৃষ্টিতে ব্যাপকভাবে সহায়তা করছে। একই সাথে এই ক্লাবটি প্রতিবছর এখানকার শিশুদের জন্য আয়োজন করছে বিভিন্ন ধরনের ওয়ার্কশপ, সেমিনার, শিক্ষা সফর, উইন্টার ক্যাম্প ইত্যাদি। পাশাপাশি বিভিন্ন জাতীয় ও আন্তর্জাতিক দিবসেও এ ক্লাবের শিশুরা নানা ধরনের কর্মকান্ডে স্বত্বঃস্ফূর্তভাবে অংশগ্রহণ করে যোগ্যতার স্বাক্ষর রেখে চলছে। সামাজিক যোগাযোগ মাধ্যমের এই যুগে আমাদের শিশুদের ঘরে থাকার প্রবণতা থেকে বেরিয়ে এসে সমবয়সী অন্যান্য শিশুদের সাথে মিশতে এই ক্লাবটি একটি সুন্দর ও কার্যকর প্লাটফর্ম সৃষ্টি করে দিচ্ছে, যা অত্যন্ত কার্যকর এবং সময়োপযোগী।</p>\r\n<p style=\"text-align: justify;\">সাভার চিলড্রেন ক্লাবের কর্মপরিধি ও সক্ষমতা আরো বৃদ্ধি লাভ করুক এবং একই সাথে এর কর্মকান্ডে আরো প্রসারিত ও অধিকতর কার্যকর হোক, সেই আশাবাদ ব্যক্ত করছি। সে সাথে আজকের নবীণ প্রজন্মের জন্য রইল নিরন্তর শুভকামনা।</p>', NULL, 'রেহানা শামীম', NULL, NULL);
