INSERT INTO `sobanetries` VALUES (415, 3, '-2024-11-03-67272fe062b8a.jpeg', 1, '2021-02-15 03:01:41', '2024-11-17 11:53:37', '২২ অগাস্ট ২০২৪', '', 10, 3, 3, NULL, '<div style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">আজকের</span><span style=\"font-family: Arial; font-size: 14pt;\"> শিশুরাই আগামী দিনের কর্ণধার। সময়ের সাথে তাল মিলিয়ে শিশুদের যোগ্য হিসেবে গড়ে তোলার প্রয়োজনীয়তা অনস্বীকার্য। আজকের শিশুরাই নেতৃত্ব দেবে আগামীর বিশ্ব। এগিয়ে নিয়ে যাবে সভ্যতা ও সংস্কৃতি। শিশুদের মানসিক ও সৃজনশীল প্রতিভার বিকাশ ঘটাতেই সেনাবাহিনীতে কর্মরত কর্মকর্তাগণের সন্তানদের বিভিন্ন প্রকার বাস্তবধর্মী প্রশিক্ষণ ও সাংস্কৃতিক মনোবৃত্তির উৎকর্ষ সাধনের জন্য গড়ে ওঠে সিলভার বেল চিলড্রেন ক্লাব, যশোর অঞ্চল। যশোর সেনানিবাসের প্রাকৃতিক মনোরোম পরিবেশে ১৯৯১ সালে প্রতিষ্ঠা লাভ করে এ ক্লাব, যা পরবর্তীতে নামকরণ করা হয় সিলভার বেল চিলড্রেন ক্লাব, যশোর অঞ্চল। জন্মলগ্ন থেকে এ ক্লাব অদ্যবধি অত্যন্ত দক্ষতার সাথে এখানকার শিশুদের জন্য নাচ, গান, অংকন, কবিতা আবৃতি, আরবি ভাষা শিক্ষা ও কারাতে এর মত বিভিন্ন বিষয়ের উপর প্রশিক্ষণ কার্যক্রম পরিচালনা করে আসছে। এ প্রশিক্ষণ আমাদের শিশুদের মাঝে সৃজনশীল মেধা বিকাশ ও প্রযুক্তিগত শিক্ষা প্রদানে বিরাট ভূমিকা রাখছে। এছাড়াও এ ক্লাবের বিভিন্ন কর্মসূচীর মাধ্যমে শিশুরা নিজেদের মাঝে বন্ধুত্বের বন্ধন সুদৃঢ় করতে সক্ষম হচ্ছে। একই সাথে ক্লাবটি বছরের বিভিন্ন সময় জাতীয় ও আন্তর্জাতিক বিভিন্ন দিবস সমূহে অনুষ্ঠিত সাংস্কৃতিক অনুষ্ঠান সমূহে শিশুদের স্বত্বঃস্ফূর্তভাবে অংশগ্রহণ এর মনোভাব তৈরি করার আপ্রাণ চেষ্টা চালাচ্ছে। পড়াশোনার পাশাপাশি এ ধরনের মেধা বিকাশে শিশুদের কর্মদক্ষতা ও ব্যক্তিগত প্রতিভার উন্নয়নে এ ক্লাবটি কার্যকরী ভূমিকা রেখেছে, যা অত্যন্ত সময়োপযোগী।সিলভার বেল চিলড্রেন ক্লাব, যশোর অঞ্চল এর কর্মদক্ষতা ও সক্ষমতা আরো বৃদ্ধি লাভ করুক এবং একই সাথে এ ক্লাবের কার্যক্রম আরও প্রসারিত হোক, সে আশাবাদ ব্যক্ত করছি। সে সাথে আজকের নবীন প্রজন্মের জন্য উজ্জ্বল ভবিষৎ কামনা করছি।</span></div>', NULL, 'শ্যামলী হায়দার ইমদাদ', NULL, NULL);
