INSERT INTO `sobanetries` VALUES (430, 2, '-2024-11-16-6738ac444f3e3.jpeg', 1, '2021-03-15 10:50:23', '2024-11-16 14:37:06', '২২ অগাস্ট ২০২৪', '', 10, 2, 3, NULL, '<p style=\"text-align: justify;\">লেডিস ক্লাব একটি অত্যন্ত গুরুত্বপূর্ণ নারী উন্নয়ন ও কল্যাণমূখী সামাজিক সংগঠন। এ সংগঠনটি সদস্যাদের আত্মবিশ্বাসী ও সামাজিকরনে বিশেষ ভূমিকা রেখে চলছে। যশোর লেডিস ক্লাবের যাত্রা শুরু ১৯৮৬ সালে। এর পর দীর্ঘ ৩৫ বছর সফলতার সাথে এগিয়ে চলছে যশোর লেডিস ক্লাব।</p>\r\n<p style=\"text-align: justify;\">যশোর লেডিস ক্লাব একটি এতিহ্যবাহী সংগঠন। বিভিন্ন অনুষ্ঠান আয়োজনের মধ্যে দিযে এ ক্লাব তার সদস্যাদের মধ্যে পারস্পারিক সৌহার্দ্য বৃদ্ধি করা ছাড়াও সামাজিক বন্ধন সুদৃঢ় করে তোলার ক্ষেত্রে ইতিবাচক ভূমিকা রেখে চলছে। সকলের মাঝে সহমর্মিতা ও সাংস্কৃতিক চর্চাসহ আর্তোন্নয়নের লক্ষ্যে কাজ করে চলছে যশোর লেডিস ক্লাব। এছাড়াও বিভিন্ন কল্যাণমূখী কর্মকান্ডে এ ক্লাবের ভুমিকা অনস্বীকার্য। বৃক্ষরোপন কর্মসূচী, অসহায় দুঃস্থদের সহযোগিতা, শীতার্তদের মাঝে শীতবস্ত্র বিতরণ এবং সদস্যাদের মানসিক বিকাশের ক্ষেত্রে বিভিন্ন প্রেষণা মূলক আলোচনার আয়োজন করা হচ্ছে এ লেডিস ক্লাবের মাধ্যমে।</p>\r\n<p style=\"text-align: justify;\">পরিশেষে যশোর লেডিস&nbsp; ক্লাবের উত্তোরত্তর মঙ্গল এবং উন্নয়ন হোক এবং যশোর লেডিস ক্লাব যাতে আরো বাস্তবমূখী এবং কল্যাণমূহী কার্যক্রমের মাধ্যমে ভূমিকা রাখতে পারে সেই প্রত্যাশা ব্যক্ত করছি।</p>\r\n<p>&nbsp;</p>', NULL, 'শ্যামলী হায়দার ইমদাদ', NULL, NULL);
