INSERT INTO `sobanetries` VALUES (500, 1, '-2022-01-19-61e7e5af3bf6a.jpeg', 0, '2021-08-19 12:11:52', '2023-08-10 01:29:04', '২৮ ডিসেম্বর ২০২১', '০১ অগাস্ট ২০২৩', 14, 1, 3, NULL, '<p style=\"text-align: justify;\"><span style=\"font-size: 14pt;\">সেনা পরিবার কল্যাণ সমিতি (সেপকস), একটি স্বপ্নের সংগঠন। 1975 সালে প্রতিষ্ঠিত এই সংগঠন গুটি গুটি পায়ে অনেক চড়াই উতরাই এর মধ্য দিয়ে সারা দেশব্যাপী বিস্তৃত হয়েছে। তারই ধারাবাহিকতায় 12 জানুয়ারি 1991 সালে সেনা পরিবার কল্যাণ সমিতি রাজেন্দ্রপুর, ঢাকা সেপকস এর একটি শাখা হিসাবে যাত্রা শুরু করেছিল। গত 18 আগস্ট 2014 সালে রাজেন্দ্রপুর শাখাকে রাজেন্দ্রপুর অঞ্চলে উন্নীত করার ফলে ক্ষুদ্র পরিসরে পদার্পণ করে স্বল্প সময়ের মধ্যেই প্রতিষ্ঠানটি শিক্ষামূলক, কল্যাণমূলক, প্রশিক্ষণমূলক, সামাজিক ও উৎসবমুখর কর্মকোন্ডে কর্মরত সেনা সদস্যদের পরিবারের কাছে বেশ জনপ্রিয় হয়ে ওঠে। বর্তমানে অঞ্চলটিতে কেন্দ্রীয় সেপকস এর নির্দেশনায় দর্জি, কাটিং, এমব্রয়ডারী (মেশিন ও হাত) এবং কম্পিউটার প্রশিক্ষণ চলমান রয়েছে। সেনা সদস্যদের পরিবারবর্গ এবং তাদের উপর নির্ভরশীল সদস্যদের (বোন এবং শ্যালিকা) স্বাবলম্বী, কর্মক্ষম, আত্মপ্রত্যয়ী করার লক্ষ্যে সেনা পরিবার কল্যাণ সমিতি, রাজেন্দ্রপুর অঞ্চল নিরলস ভাবে কাজ করে যাচ্ছে। আমি সেনা পরিবার কল্যাণ সমিতি, রাজেন্দ্রপুর অঞ্চলের উত্তরোত্তর মঙ্গল এবং সকলের সুন্দর ও আনন্দময় জীবন কামনা করি।</span></p>', NULL, 'নাহিদ রিদওয়ান', NULL, NULL);
