INSERT INTO `sobanetries` VALUES (607, 1, '-2022-08-26-6308b03ae524c.jpeg', 1, '2022-08-26 11:36:27', '2022-08-26 11:37:27', '২৪ অগাস্ট ২০২২', '', 2, 1, 3, NULL, '<p>একদা &lsquo;চন্দ্রদ্বীপ&rsquo; নামে অভিহিত ধান-নদী-খাল-এ ঘেরা বাংলাদেশের শস্য ভান্ডার বরিশাল বিভাগটি &lsquo;বাংলার ভেনিস&rsquo; হিসেবে পরিচিত ছিল। এই অঞ্চলে বরিশাল জেলার পায়রা নদীর তীরবর্তী সদ্য প্রতিষ্ঠিত শেখ হাসিনা সেনানিবাসে গত ২২ নভেম্বর ২০১৮ তারিখে সেনা পরিবার কল্যাণ সমিতি, বরিশাল অঞ্চল এর আনুষ্ঠানিক যাত্রা শুরু হয়।</p>\r\n<p>সেনা পরিবার কল্যাণ সমিতি (সেপকস) বাংলাদেশ সেনাবাহিনীর একটি গুরুত্বপূর্ণ নারী উন্নয়ন ও কল্যাণমূখী সামাজিক সংগঠন। সেপকস, বরিশাল অঞ্চলের যাত্রার শুরু হতে সেনা পরিবারের সদস্যদের স্বাবলম্বী, কর্মক্ষম, আত্মপ্রত্যয়ী এবং স্বনির্ভর করার লক্ষ্যে কাজ করে যাচ্ছে। নতুন প্রতিষ্ঠান হওয়া সত্ত্বেও সেপকস, বরিশাল অঞ্চলের সুখ্যাতি ইতোমধ্যে এই অঞ্চলের সীমা ছাড়িয়েছে। তারই ধারাবাহিকতায় দর্জি কাটিং কোর্স এবং কম্পিউটার প্রশিক্ষণ কোর্স পরিচালিত হচ্ছে। ভবিষ্যতে অন্যান্য প্রশিক্ষণ কর্মকান্ড পরিচালনার মাধ্যমে নারীজাগরণ, স্বাবলম্বীকরণ এবং সর্বোপরি আমাদের সেনাসদস্যের সহধর্মীণীদের মাঝে একতার বন্ধন, শ্রদ্ধাবোধ, আত্মবিশ্বাস ও মনোবল বৃদ্ধির প্রচেষ্টা অব্যাহত আছে। উল্লেখ্য, গত ২৮ অক্টোবর ২০২০ তারিখে সম্মানিতা পৃষ্ঠপোষক বেগম দিলশাদ নাহার আজিজ সেপকস, বরিশাল অঞ্চলের স্থায়ী ভবনের শুভ উদ্বোধন করেন, যা আমাদের জন্য একটি মাইল ফলক হিসেবে কালের স্বাক্ষী হয়ে থাকবে।</p>\r\n<p>আগামী দিনগুলিতে সেপকস, বরিশাল অঞ্চলের কার্যক্রম আরও কার্যকরীভাবে সম্পন্ন হবে এটাই আমার প্রত্যাশা। আমরা ইতোমধ্যে একটি দর্জি কাটিং এবং কম্পিউটার প্রশিক্ষণ কোর্স সফলতার সাথে শেষ করেছি।&nbsp; পরবর্তী পরিকল্পিত কোর্সসমূহ সফলতার সাথে সম্পন্ন করাই হবে আমাদের প্রধান লক্ষ্য। কঠোর অধ্যাবসায় ও আন্তরিক পরিশ্রমের মাধ্যমে সেপকস, বরিশাল অঞ্চলের কার্যক্রম সফলতা নিয়ে আসবে এ আশাবাদ ব্যক্ত করছি।</p>\r\n<p>সেপকস এর সাথে সম্পৃক্ত সম্পাদনা পর্ষদ এবং অন্যান্য সকল অফিসার, জেসিও এবং এনসিও বৃন্দকে জানাই আমার আন্তরিক অভিনন্দন ও মোবারকবাদ। আমাদের সকলের সম্মিলিত এই প্রচেষ্টা থাকুক নিরন্তর। আমি সকলের মঙ্গল কামনা করছি। মহান রাব্বুল আলামিন আমাদের সকলের সহায় হোন। আমিন।</p>', NULL, 'উম্মে কুলসুম সিমি', NULL, NULL);
