INSERT INTO `sobanetries` VALUES (695, 2, '-2024-11-06-672b2418c4fd1.jpeg', 1, '2023-09-12 15:54:35', '2024-11-06 08:09:27', '২৫ অগাস্ট ২০২৪', '', 16, 2, 3, NULL, '<p style=\"text-align: justify;\">লেডিস ক্লাব বাংলাদেশ সেনাবাহিনীর একটি ঐতিহ্যবাহী ও গুরুত্বপূর্ণ সংগঠন, যা সেনা অফিসার পরিবারবর্গের মাঝে পারস্পরিক সৌহার্দ্য, সম্প্রীতি এবং সামাজিক মেলবন্ধন দৃঢ় করতে ব্যাপক ভূমিকা পালন করে। এরই ধারাবাহিকতায় গত ১৫ ডিসেম্বর ২০১৪ তারিখ হতে লেডিস ক্লাব, বগুড়া অঞ্চলের অধীনস্থ শাখা হিসেবে রাজশাহী সেনানিবাসে আনুষ্ঠানিকভাবে লেডিস ক্লাব, রাজশাহী এর যাত্রা শুরু হয়। সময়ের পরিক্রমায় ক্লাবের পরিধি ও কার্যক্রম বৃদ্ধি পাওয়ায় প্রধান পৃষ্ঠপোষকের নির্দেশনায় অত্র লেডিস ক্লাব গত ০৩ জুলাই ২০২৩ তারিখ হতে আঞ্চলিক লেডিস ক্লাব হিসেবে প্রতিষ্ঠা লাভ করে।&nbsp;</p>\r\n<p style=\"text-align: justify;\">সামরিক জীবন অন্য যে কোন পেশাজীবীদের জীবন থেকে সম্পূর্ণ আলাদা। সেনাবাহিনী শুধু একটি পেশা নয়, এটি একটি পূর্ণাঙ্গ জীবন ব্যবস্থাও, যেখানে সামরিক অফিসারদের পাশাপাশি, অফিসার পত্নীগণও এক অবিচ্ছেদ্য অংশ। &nbsp;কারণ একজন অফিসারের পারিবারিক ও সামাজিক জীবন সামগ্রিকভাবে তার সামরিক জীবনকে উল্লেখযোগ্যভাবে প্রভাবিত করে। এই সহযাত্রায় লেডিস ক্লাব আমাদের সহমর্মিতা, পারস্পরিক শ্রদ্ধাবোধ, আন্তরিকতা এবং সর্বোপরি সেনা ঐতিহ্য লালন করার একটি সুন্দর ক্ষেত্র ও উপলক্ষ তৈরী করে দেয়। পাশাপাশি লেডিস ক্লাব অভিজ্ঞতা, তারুণ্য আর প্রাণ চাঞ্চল্যের এক অনন্য সম্মেলন, যা সুস্থ্য বিনোদন এর পরিবেশ সৃষ্টি করে। নবাগত সদস্যারা এখানে সামরিক মূল্যবোধ ও রীতিনীতি সম্পর্কে জানেন এবং পারিবারিক ও সামাজিক জীবনে তা ধারণ ও লালন করেন।</p>\r\n<p style=\"text-align: justify;\">প্রতিষ্ঠা লগ্ন থেকেই রাজশাহী লেডিস ক্লাব মৌলিক কার্যক্রমসহ অর্পিত দায়িত্ব সফলভাবে সম্পাদনের প্রচেষ্টা অব্যাহত রেখেছে। বাংলাদেশের অন্যান্য লেডিস ক্লাবের ন্যায় রাজশাহী লেডিস ক্লাবও নিয়মিত বিভিন্ন বিনোদনমূলক ও গঠনমূলক কার্যক্রম পরিচালনা করে আসছে। বর্তমানে রাজশাহী লেডিস ক্লাব শুধু সাংস্কৃতিক কর্মকান্ডের মধ্যেই নিজেদের সীমাবদ্ধ না রেখে একটি সামরিক সচেতনতামূলক প্রতিষ্ঠানে পরিণত হতে সচেষ্ট।&nbsp;</p>\r\n<p style=\"text-align: justify;\">রাজশাহী সেনানিবাস বাংলাদেশ সেনাবাহিনীর একটি ঐতিহ্যবাহী সেনানিবাস। এই সেনানিবাসের সকল পদবীর অফিসারবৃন্দ এই সেনানিবাসকে বাংলাদেশের একটি অনন্য সেনানিবাস হিসেবে গড়ে তোলার কাজে সচেষ্ট রয়েছেন। &nbsp;একজন সুযোগ্য সহধর্মিনী হিসেবে আমাদের সর্বোচ্চ চেষ্টা থাকবে তাদের এই কাজকে সফলভাবে এগিয়ে নিয়ে যাওয়ার পথের সঙ্গী হওয়া। আমি আশাবাদী যে, আমাদের সবার আন্তরিক ও স্বতঃস্ফূর্ত অংশগ্রহণ রাজশাহী সেনানিবাস লেডিস ক্লাব কে আরো প্রাণবন্ত, সৃজনশীল এবং কর্মতৎপর করে তুলবে।</p>\r\n<p style=\"text-align: justify;\">আমি ধন্যবাদ জানাচ্ছি পূর্বের শ্রদ্ধেয়া সভানেত্রী ও সহ-সভানেত্রীবৃন্দকে, যাদের অক্লান্ত পরিশ্রম ও প্রচেষ্টায় রাজশাহী সেনানিবাস লেডিস ক্লাব আজ সাফল্য ও গৌরবের এক অনন্য উচ্চতায় পৌঁছেছে। আমি এর অংশ হতে পেরে অত্যন্ত গর্বিত। আমি ধন্যবাদ জানাচ্ছি লেডিস ক্লাবের সকল সদস্যাকে, যারা নিজেরা সংগঠিত হয়ে লেডিস ক্লাবের বিভিন্ন কর্মসূচীতে উল্লেখযোগ্য ভূমিকা রেখেছেন।</p>\r\n<p>পরিশেষে লেডিস ক্লাব, রাজশাহী অঞ্চলের তথা বাংলাদেশ সেনাবাহিনীর সকল অফিসার পরিবারবর্গের মঙ্গল ও উত্তরোত্তর সমৃদ্ধি কামনা করছি। মহান আল্লাহ আমাদের সহায় হোন। আল্লাহ হাফেজ।</p>', NULL, 'সামীমা দিদার', NULL, NULL);
