INSERT INTO `sobanetries` VALUES (701, 3, '-2024-11-06-672b7264da8b8.jpg', 1, '2023-09-13 07:54:38', '2024-11-06 13:43:01', '২৫ অগাস্ট ২০২৪', '', 16, 3, 3, NULL, '<p>আজকের শিশুরাই আগামী দিনের কর্ণধার। ভবিষ্যৎ সুখী সমৃদ্ধ সোনার বাংলা গড়তে শিশুদেরকে দক্ষ, যোগ্য ও সৃজনশীল নাগরিক হিসেবে গড়ে তোলা আমাদের নৈতিক দায়িত্ব। রাজশাহী সেনানিবাসের সেনা কর্মকর্তাদের ছোট্ট শিশুদের জন্য মানসম্মত শিক্ষা, একটি নিরাপদ পরিবেশ, তাদের সুপ্ত প্রতিভাকে বিকাশ হতে দেয়ার একটি সময়োপযোগী প্লাটফর্ম হিসেবে চিলড্রেন ক্লাব, রাজশাহী অঞ্চল তার কার্যক্রম পরিচালনা করে আসছে।</p>\r\n<p>চিলড্রেন ক্লাব রাজশাহী শাখা গত ১৫ ডিসেম্বর ২০১৪ তারিখে রাজশাহী সেনানিবাসের সেনা কর্মকর্তাদের শিশু সন্তানদের নিয়ে প্রথম যাত্রা শুরু করে। চিলড্রেন ক্লাব রাজশাহী শাখা মহান স্বাধীনতার সুবর্ণ জয়ন্তীর চেতনাকে ধারন করে গত ০৩রা জুলাই ২০২৩ সালে বগুড়া অঞ্চল হতে পৃথক হয়ে সয়ংসম্পূর্ন চিলড্রেন ক্লাব রাজশাহী অঞ্চলে রূপান্তরিত হয়। শ্রদ্ধেয় প্রধান পৃষ্ঠপোষক এর সার্বিক দিকনির্দেশনা ও নিবিড় পর্যবেক্ষনের মাধ্যমে রাজশাহী চিলড্রেন ক্লাবটি একটি সময়োপযোগী ও কার্যকর প্লাটফর্ম হিসেবে কাজ করে চলেছে। শুধুমাত্র সুপ্ত প্রতিভা বিকাশের প্রশিক্ষণই নয়, জাতীয় এবং আন্তর্জাতিক দিবসে এ ক্লাবের শিশুরা নানা ধরণের প্রতিযোগিতা ও কর্মকান্ডে স্বতঃস্ফ&curren;র্তভাবে অংশগ্রহণের মাধ্যমে তাদের যোগ্যতার স্বাক্ষর রেখে চলেছে।</p>\r\n<p>শিশুদের সৃজনশীলতা, মানবিকগুন, সততা এবং সর্বশেষ দেশপ্রেম পরিস্ফুটনের মূলমন্ত্রকে কেন্দ্র করে চিলড্রেন ক্লাব রাজশাহী অঞ্চল তাদের কার্যক্রম অত্যন্ত সুনিপুনভাবে পরিচালনা করে যাচ্ছে। শিশুদের মধ্যে বন্ধুসুলভ সম্পর্ক সৃষ্টি করা, নিয়মিত চর্চার মাধ্যমে সুপ্ত প্রতিভাকে বিকশিত করা এবং সৌহার্দ ও শিষ্টাচার শেখানো এবং শ্রেণী কক্ষের একঘেয়েমী কাটিয়ে আত্নবিশ্বাসী নাগরিক হিসেবে গড়ে তোলাই আমাদের এই ক্লাবের প্রধান সংকল্প। পড়াশোনার পাশাপাশি এ ধরনের মেধা বিকাশে শিশুদের কর্মদক্ষতা ও ব্যক্তিগত প্রতিভার উন্নয়নে এ ক্লাবটি কার্যকরী ভূমিকা রেখেছে, যা অত্যন্ত সময়োপযোগী।</p>\r\n<p>পরিশেষে বলা যায় রাজশাহী চিলড্রেন ক্লাব একটি ক্লাবের চেয়েও বেশি কিছু, এটি একটি ছোট পরিবার। আমি এ প্রতিষ্ঠানের পরিচালনা পর্ষদ এবং প্রত্যেক্ষ এবং পরোক্ষভাবে জড়িত সকলকে সাধুবাদ জানাই। রাজশাহী চিলড্রেন ক্লাবের কর্মপরিধি ও সক্ষমতা আরও বৃদ্ধি লাভ করুক এবং একই সাথে এ ক্লাবের কার্যক্রম আরও প্রসারিত হোক, সেই আশাবাদ ব্যক্ত করছি। মহান আল্লাহ আমাদের সহায় হোক।</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', NULL, 'সামীমা দিদার', '1', NULL);
